INSERT INTO `content_product` (`id`, `id_language`, `group`, `name`, `description`, `image`, `url`, `vdo`, `date_add`, `date_edit`, `status`) VALUES
	(1, 1, 'Gelceuticals', 'EXO', 'สกัดจากผลไม้คุณภาพสูงทั่วโลก 17 ชนิด และมีสารต้านอนุมูลอิสระมากกว่า 200 ชนิด\r\n\r\nกระตุ้นภูมิต้านทาน ฟื้นฟูเซลล์ผิวให้แข็งแรง\r\nช่วยชะล้างทำความสะอาดเซลล์\r\nช่วยให้รู้สึกกระปรี้กระเปร่า สดชื่น กระฉับกระเฉง\r\nเพิ่มภูมิต้านทานของร่างกาย ลดอาการภูมิแพ้\r\nช่วยขจัดล้างสารพิษในร่างกาย (อนุมูลอิสระ) ทำงานได้ในระดับเซลล์\r\nช่วยในระบบไหลเวียนโลหิต ระบบความจำและสายตา\r\nช่วยชะลอความชรา ทำให้ผิวพรรณดูผ่องใส เต่งตึงขึ้น\r\nช่วยลดรอยด่างดำ และลดการอักเสบของสิว\r\nช่วยปรับระดับน้ำตาลในเลือด\r\nช่วยปรับระดับความดันให้เป็นปกติ\r\nช่วยลดไขมันในเส้นเลือด\r\nช่วยให้ระบบขับถ่ายดีขึ้น\r\nช่วยลดอาการอักเสบต่างๆ\r\nช่วยลดภาวะเสี่ยงของเส้นเลือดหัวใจตีบ\r\nลดอัตราเสี่ยงการเกิดมะเร็ง\r\nมีกรดอะมิโน มิวโครโพลีซัคคาร์ไรด์ และโพลีซัคคาร์ไรด์ที่จำเป็นต่อระบบการเผาผลาญพลังงาน\r\nอุดมไปด้วยวิตามิน, เกลือแร่, กรดอะมิโน, เบต้าแคโรทีน, วิตามิน E และ C', '', 'http://www.agel.com/pdfs/core/EXO-Facts.pdf', 'http://www.youtube.com/watch?v=ysbxzMERzaI', '0000-00-00 00:00:00', '2010-11-28 04:32:39', 1),
	(2, 1, 'Gelceuticals', 'UMI', 'สารต้านอนุมูลอิสระของฟูคอยแดน (Fucoidan) ทำให้นักวิทยาศาสตร์และนักค้นคว้าทั้งหลาย หันมาให้ความสำคัญและสนใจ เนื่องจากฟูคอยแดนเป็นหนึ่งในสมบัติอันล้ำค่าอจากท้องทะเลลึก ที่ให้คุณประโยชน์ต่อร่างกายมนุษย์มากที่สุด\r\n\r\nมีฤทธิ์ป้องกันการแข็งตัวเกล็ดเลือด\r\nกระตุ้นโปรตีน และเอนไซม์ปกติให้กลายเป็นภูมิต้านทาน\r\nสามารถกระตุ้นเซลล์ต้นกำเนิด (Stem Cell) ให้ออกมาจากไขกระดูกได้\r\nต้านการเกิดเนื้องอก และการเจริญเติบโตของเซลล์มะเร็ง\r\nต้านการสร้างหลอดเลือดในก้อนมะเร็ง\r\nลดอัตราเสี่ยงการเกิดมะเร็งเต้านม\r\nต้านเชื้อไวรัส และเชื้อแบคทีเรีย\r\nกระตุ้นเซลล์ภูมิต้านทาน\r\nต้านการเกิดภูมิแพ้\r\nปกป้องการทำงานของตับ\r\nทำลายอนุมูลอิสระ\r\nลดอัตราเสี่ยงโรคหัวใจ ระบบทางเดินหายใจ และเบาหวาน\r\nช่วยในภาวะข้ออักเสบ\r\n', '', 'http://www.agel.com/pdfs/core/UMI-Facts.pdf', 'http://www.youtube.com/watch?v=RNSCm7R95dI', '0000-00-00 00:00:00', '2010-11-28 04:32:08', 1),
	(3, 1, 'Gelceuticals', 'MIN', 'ประกอบด้วยวิตามินและเกลือแร่ที่เหมาะสมและเพียงพอในการบำรุงร่างกายในแต่ละวัน สารสำคัญต่างๆ ที่ผลิตจากธรรมชาติในรูปของเจล สามารถดูดซึมเข้าสู่ร่างกายได้อย่างรวดเร็วกว่าการย่อยผ่านระบบกระเพาะอาหาร และลำไส้\r\n\r\nเสริมสร้างภูมิคุ้มกันของร่างกาย\r\nบำรุงสายตา, เหงือก, ฟัน และไขข้อ\r\nเสริมการดูดซึมธาตุเหล็ก และแคลเซียม\r\nช่วยในการต่อต้านอนุมูลอิสระ\r\nบำรุงเม็ดเลือดแดง\r\nส่งเสริมระบบประสาท\r\nบำรุงผิว, ผม, เล็บ\r\nลดน้ำตาลในกระแสเลือด\r\nช่วยให้เลือดแข็งตัวเมื่อเกิดแผล\r\nลดอัตราเสี่ยงการเกิดมะเร็งเต้านม\r\nมีเอนไซม์ช่วยในการแบ่งตัวของเซลล์\r\nลดอาการปวดเกร็งของกล้ามเนื้อ\r\nลดอัตราเสี่ยงของการเป็นหมัน\r\n', '', 'http://www.agel.com/pdfs/core/MIN-Facts.pdf', 'http://www.youtube.com/watch?v=VAqJhtQasmQ', '0000-00-00 00:00:00', '2010-11-28 04:32:26', 1),
	(4, 1, 'Gelceuticals', 'FIT / SLM', 'เพื่อรูปร่างดี และช่วยควบคุมน้ำหนักอย่างมีประสิทธิภาพสูงสุด ส่วนประกอบสำคัญ คือ Garcinia cambogia (ส้มแขก) ช่วยในการยับยั้งการเปลี่ยนแป้งไม่ให้เป็นไขมันสะสม เป็นตัวสร้าง Serotonin ยับยั้งการอยากอาหาร ทำให้ไม่หิว เพิ่มการสันดาปไขมัน(เก่า) ช่วยลด cholesterol ทำให้รูปร่างสมส่วน มีทรวดทรงสวยงาม\r\n\r\nลดน้ำหนักและกระชับสัดส่วน\r\nลดความอยากอาหารโดยไม่กระทบระบบประสาทส่วนกลาง\r\nลดการสะสมไขมันใหม่\r\nเผาผลาญไขมันส่วนเกิน\r\nไม่มีผลข้างเคียง\r\n', '', 'http://www.agel.com/pdfs/active/FIT-Facts.pdf', 'http://www.youtube.com/watch?v=p5wIse4iXTs', '0000-00-00 00:00:00', '2010-11-28 04:31:51', 1),
	(5, 1, 'Gelceuticals', 'OHM', 'เป็นการผสมผสานระหว่างวิตามิน แร่ธาตุและสมุนไพรที่มีส่วนผสมของโคเอนไซม์ ซึ่งช่วยในการเร่งกระบวนการขอคาร์โบไฮเดรต ให้เป็นพลังงานอย่างเร่งด่วน นอกจากนี้ ยังมีแพเนคจินเซ่ง (โสม) ของ Agel OHM ที่สามารถเพิ่มความต้านทานในการคลายเครียด อีกทั้งยังช่วยเพิ่มประสิทธิภาพทางร่างกายและจิตใจให้แก่ผู้ใช้ โดยให้รับประทานควบคู่กับอาหารดี และมีประโยชน์ และออกกำลังกายในขณะเดียวกัน เพื่อประโยชน์สูงสุด\r\n\r\nเพิ่มพลังงานให้กับร่างกายอย่างเร่งด่วน\r\nมีสมาธิในการทำงานดีขึ้น\r\nช่วยให้กระปรี้กระเปร่า ไม่รู้สึกอ่อนเพลียระหว่างวัน\r\nสำหรับนักกีฬาหรือผู้ที่สูญเสียเหงื่อ ใช้ทดแทนได้เป็นอย่างดี\r\nช่วยให้ระบบขับถ่ายดีขึ้น\r\nช่วยให้สดชื่น กระฉับกระเฉงอย่างรวดเร็ว\r\n', '', 'http://www.agel.com/pdfs/active/OHM-Facts.pdf', 'http://www.youtube.com/watch?v=omDCHTP8KSU', '0000-00-00 00:00:00', '2010-11-28 04:31:10', 1),
	(6, 1, 'Gelceuticals', 'FLX', 'มีส่วนประกอบสำคัญที่ได้จากธรรมชาติทั้งหมด 4 ชนิด ซึ่งช่วยในระบบการเคลื่อนไหวของร่างกาย ระบบการหล่อลื่น และบำบัดเมื่อข้อต่อเกิดอาการผิดปกติ พร้อมด้วยเทคโนโลยีของเจล นิวตริชั่นนอลทำให้การทำงานของส่วนประกอบทั้ง 4 นั้น ให้ผลที่รวดเร็ว โดยไม่มีผลข้างเคียงใดๆ ต่อระบบอวัยวะอื่นๆ ในร่างกาย เพื่อสุขภาพที่ดีที่สุดของกระดูกและข้อต่อ\r\n\r\nCeladrin เป็นสารสกัดจากธรรมชาติทั้งหมด ที่ช่วยพัฒนากระบวนการเสริมสร้างและซ่อมแซมกระบวนการหล่อลื่นของข้อต่อ\r\nGlucosamine & Chondroitin ช่วยเสริมสร้าง ซ่อมแซม บำรุงกระดูกอ่อนให้กลับมามีสุขภาพดีและแข็แรงเหมือนเดิม\r\nMethysulfonylmethane เป็นกรดอะมิโนซึ่งได้มีการทดลองทางการแพทย์พบว่า ช่วยในการลดอาการอักเสบได้จริง\r\n', '', 'http://www.agel.com/pdfs/thrive/FLX-Facts.pdf', 'http://www.youtube.com/watch?v=9aES2h_uOmg', '0000-00-00 00:00:00', '2010-11-28 04:33:30', 1),
	(7, 1, 'Gelceuticals', 'HRT', 'เจลบำบัดที่ผ่านการวิจัย และใช้เทคโนโลยีขั้นสูงในการผลิต เพื่อปกป้องหัวใจของคุณ ให้พลังงานกับกล้ามเนื้อหัวใจและรักษาดุลยภาพของคอเสเตอรอล เพื่อปกป้องที่ดีที่สุดของหัวใจคุณ มีส่วนผสมของสารต้านอนุมูลอิสระและสารอาหารบำบัด อันทรงคุณประโยชน์ เพื่อให้พลังงาน และปกป้องหัวใจของคุณ ได้แก่\r\n\r\nTaurine สามารถลดความเสียหายที่เกิดกับหัวใจ\r\nCarnitine ช่วยเพิ่มพลังงานใกล้ามเนื้อหัวใจ\r\nCo Q10 เป็นสารต้านอนุมูลอิสระอันทรงพลัง ช่วยเป็นเชื้อเพลิงให้กับ Mitochondria และป้องกันภาวะหัวใจล้มเหลว\r\n', '', 'http://www.agel.com/pdfs/thrive/HRT-Facts.pdf', 'http://www.youtube.com/watch?v=UDhtlqTpuio', '0000-00-00 00:00:00', '2010-11-28 04:32:59', 1),
	(8, 1, 'Gel Care', 'Gentle Daily Cleansing Gel', 'กำจัดสิ่งสกปรกด้วยเจลโฟมบริสุทธิ์ ใช้ล้างเครื่องสำอาง ช่วยขจัดเซลล์ตาย สารตกค้างจากสิ่งแวดล้อม', '', 'http://www.agel.com/files/pdfs/Ageless_Cleanser.pdf', 'http://www.youtube.com/watch?v=8Ed_JzkOSpA', '0000-00-00 00:00:00', '2010-11-28 04:45:00', 1),
	(9, 1, 'Gel Care', 'Revitalizing Gel Scrub', 'ขัดผิวให้สดใสด้วยความนุ่มนวล จึงสามารถขัดผิวได้ 4-5 ครั้งต่อสัปดาห์', '', 'http://www.agel.com/files/pdfs/Ageless_Scrub.pdf', 'http://www.youtube.com/watch?v=8Ed_JzkOSpA', '0000-00-00 00:00:00', '2010-11-28 04:45:00', 1),
	(10, 1, 'Gel Care', 'Refreshing Misting Gel', 'ปรับความตึงตัวและสมดุลให้ผิวพรรณด้วยละอองเจล ใช้หลงจากล้างหน้าทุกครั้ง', '', 'http://www.agel.com/files/pdfs/Ageless_Myst.pdf', 'http://www.youtube.com/watch?v=8Ed_JzkOSpA', '0000-00-00 00:00:00', '2010-11-28 04:45:01', 1),
	(11, 1, 'Gel Care', 'Age-defying Eye Gel', 'สร้างความสดใสให้กับรอบดวงตา ลดริ้วรอย เพิ่มความยืดหยุ่นรอบดวงตา ใช้วัละ / ครั้ง เช้าและก่อนนอน', '', 'http://www.agel.com/files/pdfs/Ageless_AgeDefying.pdf', 'http://www.youtube.com/watch?v=8Ed_JzkOSpA', '0000-00-00 00:00:00', '2010-11-28 04:45:01', 1),
	(12, 1, 'Gel Care', 'Intensive Gel Serum', 'ช่วยให้ผิวอ่อนเยาว์ขึ้นด้วยวิตามิน และสารต้านอนุมูลอิสระ ใช้วันละ 1 ครั้งหลังจากใช้ Anti-oxidant Misting Gel  ', '', 'http://www.agel.com/files/pdfs/Ageless_Serum.pdf', 'http://www.youtube.com/watch?v=8Ed_JzkOSpA', '0000-00-00 00:00:00', '2010-11-28 04:45:02', 1),
	(13, 1, 'Gel Care', 'Daily Moisturizing Gel', 'ปกป้องอุมูลอิสระที่มาทำลายผิวและช่วยทำให้ผิวชุ่มชื้น ใช้วันละ 1 ครั้ง ตอนเช้า', '', 'http://www.agel.com/files/pdfs/Ageless_Moisturizer.pdf', 'http://www.youtube.com/watch?v=8Ed_JzkOSpA', '0000-00-00 00:00:00', '2010-11-28 04:45:02', 1),
	(14, 1, 'Gel Care', 'Nourishing Night Gel', 'เร่งการสร้างเซลล์ใหม่ขณะนอนหลับ ใช้ก่อนนอน', '', 'http://www.agel.com/files/pdfs/Ageless_NightGel.pdf', 'http://www.youtube.com/watch?v=8Ed_JzkOSpA', '0000-00-00 00:00:00', '2010-11-28 04:45:03', 1),
