INSERT INTO `content_testimonial` (`id`, `id_language`, `name`, `job`, `description`, `id_product`) VALUES
	(1, 1, 'คุณภิญญา ชัยสงคราม ', 'โปรแกรมเมอร์', 'รับประทาน Agel EXO และใช้ AGELess skin care ติดต่อกันเป็นเวลา 4 อาทิตย์ และ เป็นคนไม่ค่อยแข็งแรงค่ะ นอนดึกเป็นหวัด ตากฝนเป็นไข้ ป่วยเกือบทุกเดือน ป่วยทีนึง 2-3 สัปดาห์ แต่หลังจากได้ทาน EXO เป็นประจำ ก็ทำให้ร่างกายแข็งแรงขึ้นมาก', '1,15'),
	(2, 1, 'คุณปูชิตา สุขีวัฒนา', 'ประกอบธุรกิจส่วนตัว', 'เมื่อก่อนเป็นโรคภูมิแพ้หนักมาก ทุกครั้งที่นอนดึก พอตื่นเช้าขึ้นมา จะมีอาการคล้ายเป็นหวัด คัดจมูก จามมีน้ำตาไหลออกมาตลอดและมีสีหน้าที่ซีดเซียว เหมือนคนไม่สบาย ตั้งแต่ทาน EXO เป็นประจำ ประมาณ 3 สัปดาห์ อาการคล้ายจะเป็นหวัด คัดจมูกก็หายไป ทกๆวัน สดชื่นขึ้นมากค่ะ', '1'),
	(3, 1, ' คุณไมยสิทธิ์ สว่างธรรมรัตน์', 'ประกอบธุรกิจส่วนตัว', 'แต่ก่อนผมเป็นคนหน้ามัน มีสิวอักสบมาก หาหมอรักษาสิวและทานยาเป็นประจำ แต่พอได้เริ่มทาน EXO เป็นประจำทุกวัน ตลอดระยะเวลา 3 เดือน ทำให้สิวอักเสบและความมันบนใบหน้าของผมเริ่มลดน้อยลง สภาพผวหน้าดีขึ้นมากครับ', '1'),
	(4, 1, 'คุณณัฏฐ์เดช ชัยปกรณ์วงศ์ ', 'ประสานงานธุรกิจต่างประเทศ', 'ปกติเป็นคนหน้ามันมาก และมีสิวหัวช้างเป็นหัวหนองเกิดขึ้นบ่อยๆ ผุดๆ หายๆ ตามบริเวณ T-Zone เวลานอนดึก แต่พอได้ทาน EXO เป็นประจำทุกวัน เพียงแค่ 1 เดือน นอนดึกหรืออดนอน สิวแทบจะไม่ขึ้นเลย หน้าผ่องใสขึ้น จนมีคนรอบข้างทักเยอะมากว่าไปทำอะไรกับหน้ามา ส่วน UMI ทานตอนไม่ค่อยสบาย เพราะเป็นคนเลือดจาง ป่วยง่าย เหนื่อยง่าย ก็ทำให้ร่างกายฟื้นตัวภายในชั่วข้ามคืน', '1'),
	(5, 1, 'คุณริณรณัชย์ วรรัฐศุภการย์ ', 'นักเคมี', 'เคยทานอาหารเสริมควบคุมน้ำหนักมาหลายๆ แบรนด์มากค่ะ ทุกครั้งที่ทานต้องมีความตั้งใจสูงมาก แต่อดทนได้แค่ 2 อาทิตย์ก็เลิกทานแล้วเพราะไม่อร่อยและขั้นตอนในการทานที่ยุ่งยากตั้งแต่การเตรียมจนถึงการล้างอุปกรณ์ ทำให้เสียเวลาในชีวิตมาก แต่เมื่อได้ลองทาน SLM ชอบมากๆเลยค่ะ รสชาดอร่อย เพียงเด็ดแล้วดูดเท่านั้น', '4'),
	(6, 1, 'คุณนพรัตน์  ทองศักดิ์', 'วิศวกร', 'ก่อนนี้ตื่นมาตอนเช้าจะมีน้ำมูกใสๆ เวลาเข้าห้องประชุมจะไอและจาม อากาศเปลี่ยนแปลงนิดหน่อย จะเป็นหวัดได้ง่าย ทำให้ไม่ค่อยมีความสุขทุกครั้งที่มีลมเย็นๆ หรืออากาศเปลี่ยน พอได้ EXO และ UMI ไม่ทันได้สังเกตอาการก็ดีขึ้น ไม่ชอบการอักเสบครับ', '1'),
	(7, 1, 'นพ.สาโรจน์ คินธร', 'แพทย์ประจำห้องฉุกเฉิน รพ.เอกชน กรุงเทพฯ', 'ในเรื่องอาหารเสริม ผมเป็นคนหนึ่งที่ไม่เคยเชื่อมาก่อนว่ามันจะได้ผลจริง แต่ด้วยความที่ผมเป็นนักวิทยาศาสตร์ ผมก็พิสูจน์ด้วยตัวเอง อ่านข้อมูล ผมได้ลองใช้ SLM ในเวลาเพียงแค่ 2 เดือน น้ำหนักผมลดไป 8 กิโลกรัม หลังจากนั้นผมลองหยุดทาน ดูว่ามันจะเกิดอาการโยโย้เอฟเฟคไหม ผมหยุดมาตอนนี้ 6 เดือนแล้วครับ น้ำหนักก็ไม่ขึ้นครับ จึงทำให้ผมประทับใจใน SLM ', '4'),
	(8, 1, 'ภญ.ณัฏฐนิช นิลพงษ์', 'เจ้าของร้านยาและอุปกรณ์การแพทย์ 2 แห่ง', 'อาชีพเราจ่ายยาให้กับคนป่วยหลังจากที่เค้าป่วยไปแล้ว แต่อาหารเสริมเป็นการป้องกันก่อนที่จะป่วย และเสริมสร้างสุขภาพของตัวเองให้แข็งแรงขึ้นหลังจากที่ได้รู้จักกับ Agel และได้ใช้ผลิตภัณฑ์พบว่าตัวเองป่วย น้อยลงมากและเปลี่ยน จากวิตามินแบบเม็ดมาเป็น MIN แทน เพราะดูดซึมได้ดีกว่า .....ขอขอบคุณ Agel ที่ช่วยให้เราแข็งแรง', '3'),
	(9, 1, 'คุณนพดล สมเจริญศิลป์', 'ธุรกิจส่วนตัว จ.สุพรรณบุรี', 'ผลิตภัณฑ์ที่ประทับใจคือ EXO ในตอนแรกผมมีปัญหาเกี่ยวกับเรื่องผิวพรรณ ปกติถ้าเป็นหน้าหนาว ผิวก็จะแห้งมาก หลังจากที่ผมใช้ EXO ได้ประมาณ 15 ซอง ผมรู้สึกว่าผิวดีขึ้น กระจ่างใสขึ้น เริ่มมีคนทัก ผมต้องขอขอบคุณ EXO ด้วยครับ', '1'),
	(10, 1, 'คุณศิวภัสร์ ไพศาลสิรินกุล', 'ธุรกิจนำเข้ารถยนต์', 'เมื่อก่อนร่างกายผมจะมีอาการโรคเหน็บชาบ่อยมากที่แขนและขา หลังจากผมได้ทาน MIN ไปแล้ว เป็นระยะเวลาประมาณ 1 เดือน อาการชาก็หายไป ในตัว MIN จะมีวิตามินที่ร่างกายสามารถรับได้ 100% และมีแร่ธาตุ 75% หลังจากที่ผมได้ทานแล้ว อาการทุกอย่างผมก็หายไป ', '3'),
	(11, 1, 'คุณฐิตารีย์ พรหมสุวรรณ', 'อดีตเจ้าของธุรกิจส่วนตัว', 'ผลิตภัณฑ์ที่ประทับใจคือ MIN ค่ะ เพราะเมื่อก่อนจะมีปัญหาเรื่องสุขภาพ นอนกลางคืน เวลาเปิดแอร์แล้วมือจะชา พอทาน MIN ไปสักประมาณ 1 เดือน อาการเหล่านี้ก็จะหายไป ขอบคุณ MIN ค่ะ', '3'),
	(12, 1, 'คุณคณภัทร แพงพงา', 'ประกอบธุรกิจส่วนตัว', 'ประทับใจที่สุดคือตัว FLX ครับ ผมเองมีปัญหาเรื่องเข่ามานานแล้ว ตอนเป็นวัยรุ่น เตะบอลแล้วก็ไปผ่าเข่ามา ทำให้น้ำเลี้ยงข้อเข่าไม่ค่อยดี 10 กว่าปีที่ผมตีแบดมา แล้วก็มีปัญหาเรื่องเข่ามาตลอด วันนี้ได้ทาน FLX ทำให้ผมมีเข่าที่แข็งแรง แล้วก็กลับไปตีแบดได้อย่างไม่มีปัญหาเลยครับ ขอบคุณ FLX ครับ', '6'),
	(13, 1, ' คุณกวินพัฒน์ อัศวกิตติโสภณ', 'ติวเตอร์', 'ผลิตภัณฑ์ที่ประทับใจ คือ UMI เพราะเมื่อก่อนตอนที่ผมเป็นติวเตอร์ใหม่ๆ ผมสอนอย่างหนักมาก แล้วทุกครั้งทีผมสอนผมก็จะใช้ปากกาหมึกเคมี แล้วก็สูดมันเข้าไปทุกวัน ปรากฏว่าคอผมเลยอักเสบมาก แล้วผมจึงไปตรวจที่โรงพยาบาลพบว่าคอผมแดงหมดเลย ทำให้ผมมาทดลอง UMI ครับ มันสามารถช่วยให้อาการอักเสบหายไปได้ ขอบคุณ UMI มากครับ', '2'),
	(14, 1, 'คุณชญาณ์นันท์', 'พนักงานไอที กรุงเทพฯ', 'ก่อนหน้านี้ดาวได้ทดลองลดน้ำหนัก ทั้งทางที่ดีและไท่ดีมาแล้ว ปรากฏว่าเกิดอาการโยโย่เอฟเฟค น้ำหนักขึ้นมาประมาณ 15 - 20 กิโลกรัม แต่ว่าพอลองทาน SLM ได้ 3 เดือน น้ำหนักก็ลดได้ถึง 10 กิโลกรัม ต้องขอขอบคุณ SLM มากๆ คะ', '4'),
	(15, 1, 'คุณไกรฤกษ์ ทิมอ่อน', 'พนักงานประจำบริษัทเอกชน', 'ตัวที่ประทับใจคือ ชุด Ageless Skin Care คุณอาจจะแปลกใจว่าทำไมผู้ชายใช้ชุด Ageless จะบอกว่าปัจจุบันนี้ผู้ชายที่ดูแลตัวเอง ก็สามารถใช้เครื่องสำอางได้ครับ ที่ประทับใจเพราะตัวเองเป็นคนที่มีริ้วรอยบนใบหน้าเยอะ หลังจากที่ใช้ชุด Ageless ได้ 2 วัน ก็รู้สึกว่าหน้าตึงขึ้นและริ้วรอยก็จางลง ต้องขอขอบคุณ Ageless ครับ', '15'),
	(16, 1, 'คุณสวัสดิ์ ศรีสาคร', 'อดีตรับราชการทหารเรือ', 'ผลิตภัณฑ์สารอาหารแบเจล ผมชอบทุกตัวครับ เพราะรสชาติอร่อย มีสีสันหลากหลาย จดจำได้ง่าย ที่ผมใช้อย่างต่อเนื่องก็จะเป็นตัว EXO กับ UMI โดยปกติช่วงอากาศเปลี่ยนแปลงผมจะเป็นหวัด ปีละ 2-3 ครั้ง แต่หลังจากที่ผมใช้ EXO กับ UMI มา ใน 1 ปีผมก็ไม่เป็นหวัดหรือเจ็บป่วยอีกเลยครับ ขอบคุณครับ', '1,2'),
	(17, 1, 'คุณจักรกฤษณ์ วงศ์ศักดา', 'ประกอบธุรกิจส่วนตัว', 'ผลิตภัณฑ์ที่ประทับใจคือ ตัว HRT วันหนึ่งผมเข้าไปฉีดยากันยุงในห้องลูก แล้วปรากฏว่าหายใจผิดจังหวะ พอได้ทาน HRT ไปหนึ่งซองเท่านั้น ประมาณ 10 นาที จากที่หายใจแล้ว แปร๊บ แบบรุนแรง พอหายใจอีกทีมันก็ดีขึ้น ผมรู้สึกว่า HRT ทำให้ผมประทับใจมากครับ', '7'),
	(18, 1, 'คุณกิตติพัฒน์', 'เจ้าของบริษัทออกแบบเว็บไซต์', 'สินค้าที่ประทับใจมากคือ UMI เพราะว่าก่อนหน้านี้ผมมีปัญหาเรื่องของทอนซิลอักเสบ พอมาใช้ UMI 1 ซองก็ทำให้อาการดีขึ้น จากที่เคยต้องให้เวลารักษาประมาณ 3-4 วัน ขอบคุณครับ', '2'),
	(19, 1, 'คุณอคิรณัฐ สุทธิพงษ์เกษตร', 'ประกอบธุรกิจส่วนตัว', 'ผมเป็นวัยรุ่นชอบนอนดึก แล้วก็จะมีแผลร้อนในปากค่อนข้างเยอะ แต่พอได้รู้จักกับตัว MIN ก็ทาน MIN มาประมาณ 1 เดือน นอนดึกเหมือนเดิม แต่แผลร้อนในหายไปหมดเลยครับ ตอนนี้ก็เลยประทับใจในตัว MIN มากๆ ขอบคุณครับ', '3'),
	(20, 1, 'คุณพิราวรรณ์ เปรมศรีโรจน์', 'แม่บ้าน จ.ภูเก็ต', 'ประทับใจตัว  EXO ค่ะ ก่อนหน้านี้มีอาการปวดประจำเดือน เวลามีประจำเดือนจะต้องกินยาต่อเนื่องกันถึง 3 วัน วันละ 3 มื้อ เพื่อบรรเทาอาการปวด แต่หลังจากได้ลองทาน EXO เพียงแค่เดือนแรกเท่านั้น ก็ทำให้รู้สึกว่าอาการปวดน้อยลง ยาก็ทาเพียงแค่เม็ดเดียว พอทานต่อเนื่องมาเดือนที่ 2 ก็ไม่ต้องทานยาแก้ปวดอีกเลย ขอบคุณ EXO ค่ะ ', '1'),
	(21, 1, 'คุณภัชชภร พิบูลศุภธนา', 'ประกอบธุรกิจส่วนตัว', 'ผลิตภัณฑ์ที่ประทับใจคือ EXO ค่ะ เพราะเมื่อก่อนเป็น Call Center จะเจอกับคอมพิวเตอร์และโทรศัพท์เยอะมาก ทำให้เป็นไมเกรน แต่พอทาน EXO ได้ประมาณ 2 เดือน ไมเกรนก้หายไปเลยค่ะ ขอบคุณ EXO', '1'),
	(22, 1, 'คุณกาญต์ เตชะบูรณเทพาภรณ์', 'อดีต Call Certer', 'ผมได้ให้คุณแม่ผมทาน SLM ก่อนหน้านี้แม่ผมมีปัญหาเกี่ยวกับเรื่องของตัวไตรกลีเซอไรด์ที่สูงเกินไปถึง 120 กว่าๆ หลังจากที่ทาน SLM ไปแค่เดือนเดียวเท่านั้น ไตรกลีเซอไรด์ก็ลดลงเหลือแค่ 170 กว่าๆ สิ่งนี้พิสูจน์ได้ว่า ตัว SLM นี่ได้ผลจริงๆครับ ขอบคุณครับ', '4'),
	(23, 1, 'คุณวรรทนา อัศกรกิ่ง', 'อดีตเซลอุปกรณ์การแพทย์ และเจ้าหน้าที่ห้องแล็บ', 'ก่อนหน้านี้มีปัญหาเรื่องปลายประสาทอักเสบ ชาซีกขวาก็มาทาน EXO กับ HRT คู่กัน ช่วงเวลาแค่เดือนสองเดือน อาการดีขึ้เกือบ 100% ตอนนี้ก็ทานมา 1 ปีแล้ว อาการดีขึ้นมาก ขอบคุณค่ะ', '1,7'),
	(24, 1, 'คุณศรัญญา คำหล้า', 'นักศึกษา', 'ผลิตภัณฑ์ที่ประทับใจคือ EXO โดยตัวเองแล้วเป็นคนที่แอนตี้อาหารเสริมทุกชนิด เพราะว่าอยู่ในรูปแบบเม็ดบ้าง ผงบ้าง ทานลำบากแต่พอมาเจอตัวนี้ก็ง่ายค่ะ สะดวกในการพกพา ปัจจุบันมีปัญหาเรื่องสิว ก็ช่วยให้สิวยุบลง รักษาหน้าให้ดีขึ้น ขอบคุณ EXOค่ะ', '1'),
	(25, 1, 'คุณนภัสศรันย์ ชยาภรณ์ โปห์', 'อดีตผู้ช่วยผู้บริหารโรงแรม กรุงเทพฯ', 'ชื่นชอบในผลิตภัณฑ์ EXO ค่ะ ลูกชายของดิฉันเองที่ทานเพียง 1 อาทิตย์ สามารถหายจากโรคเหงือก ภูมิแพ้ ตรงนี้ทำให้ประทับใจมากๆค่ะ', '1'),
	(26, 1, 'นพ.สุเพียว อึ๊งวิจารย์ปัญญา', 'ผู้อำนวยการโรงพยาบาล จ.กาญจนบุรี', 'แพทย์ส่วนใหญ่มักไม่เชื่อเรื่องอาหารเสริม แต่สุขภาพของคุณพ่อซึ่งป่วยเป็นโรคหัวใจ ตอนนั้นหนักมากจนรู้สึกหมดหวัง ผมได้รู้จักหนังสือเล่มหนึ่ง หนังสือเล่มนี้ชี้ให้เห็นคุณประโยชน์ของอาหารเสริมที่นำมาบำบัดโรคได้ จึงได้ศึกาอาหารเสริมอย่างจริงจัง จนได้พบกับอาหารเสริมแบบเจล 1 ปีเศษที่ได้ใช้ผลิตภัณฑ์ถือว่าคุ้มค่ามาก ถ้าไม่รู้จักอาหารเสริม ไม่รู้จักคอนเซ็ปของอาหารเสริม และไม่รู้จักอาหารเสริมแบบเจล คุณพ่อของผมคงไม่ดีขึ้นขนาดนี้ สารอาหารแบบเจล เป็นทางเลือกสำหรับทุกคน สารอาหารแบบเจลให้สุขภาพที่ดีแก่ผมและครอบครัวได้เปิดโอกาสให้ผมพบทางเลือกใหม่ๆ ผมและครอบครัวขอขอบคุณครับ', '0'),
	(27, 1, 'นพ.สิทธวีร์ เกียรติชวนันต์', 'แพทย์ผู้เชี่ยวชาญด้านหู คอ จมูก เจ้าของคลินิกความงาม 5 แห่ง', 'ที่ผ่านมาเกือบ 20 ปี ผมเน้นในการรักษาโรคก็จะพบว่าที่เจ็บไข้ได้ป่วยแล้วจึงเข้ามาหาเรา โรคเรื้อรังต่างๆ ซึ่งเป็นโรคที่ไม่หายขาด คนใช้ต้องกินยาตลอดชีวิต เช่น โรคเบาหวาน,ความดันโลหิตสูง,โรคหัวใจ,เส้นโลหิตแตก ตีบ ตัน     หรือโรคภูมิคุ้มกันผิดปกติ สิ่งที่ยอดเยี่ยมมากๆ คือ ปัจจุบันผมได้ความรู้ในอีกช่องทางหนึ่ง คือ ความรู้ในการป้องกันโรค ได้รู้วิธีในการป้องกันโรคที่ถูกต้องโดยใช้ผลิตภัณฑ์สารอาหารแบบเจล เพราะผลิตภัณฑ์นี้ถูกออกแบบมาเพื่อป้องกันโรคเรื้อรังต่างๆ ที่เป็นสาเหตุให้คนเสียชีวิต ผมรู้สึกมีความสุขมากๆ ที่มีโอกาสนำเสนอถ่ายทอดเรื่องราวตรงนี้ออกไปเพื่อช่วยเหลือคนอื่นให้เขามีความรู้ในการป้องกันโรค เพื่อหลีกเลี่ยงโรคเสื่อมเรื้อรังอันเป็นสาเหตุของการเสียชีวิต', '0'),
	(28, 1, 'นพ.ชนินทร์ ลีวานันท์', 'แพทย์เวชศาสตร์ฟื้นฟู โรงพยาบาลรัฐบาล กรุงเทพฯ', 'เดี่ยวนี้คนเป็นโรค อายุน้อยๆทั้งนั้น สังเกตคนอายุ 30,40 ปี เป็นมะเร็ง อัมพาต ไม่ต้องดูที่ไหนไกล เพื่อนหมออายุแค่ 40 กว่าๆ เป็นอัมพาตแล้ว อาจารย์แพทย์หลายท่านยังไม่เกษียณแต่เวียชีวิตด้วยโรคมะเร็ง เพราะอะไร เพราะเราลืมดูแลตัวเองและวิธีหนึ่งที่จะสามารถดูแลตัวเองได้ คือการใช้อาหารเสริมที่มีคุณภาพสูงมากๆ สารอาหารแบบเจลเป็นอาหารเสริมตัวหนึ่งที่คุณภาพสูงมากๆ ดูดซึมได้ยอดเยี่ยม ผลิตภัณฑ์ถูกออกแบบมาเพื่อเลียนแบบธรรมชาติ เป็นอาหารที่ย่อยให้เรียบร้อยแล้ว สารอาหารที่ดูดซึมแล้ว ร่างกายนำไปใช้ได้ทันที ผมขอยืนยันว่า สารอาหารแบบเจลคือ ผลิตภัณฑ์ที่ดีที่สุดตัวหนึ่ง ถ้าไม่มั่นใจคุณต้องพิสูจน์ด้วยตนเอง', '0'),
	(29, 1, 'นพ.สาโรจน์ คินธร', 'แพทย์ประจำห้องฉุกเฉิน โรงพยาบาลเอกชน กรุงเทพฯ', 'ในเรื่องอาหารเสริม ผมเป็นคนหนึ่งที่ไม่เคยเชื่อมาก่อนว่ามันจะได้ผลจริงแต่ด้วยความที่ผมเป็นนักวิทยาศาสตร์ ผมไม่ได้เอาความที่ไม่เชื่อของผมไปหักล้างกับสิ่งที่เขาบอกมา ผมก็พิสูจน์ด้วยตัวเองอ่านข้อมูลด้วยตัวเอง ผมได้ลองใช้ SLM ครับในเวลาเพียงแค่ 2 เดือน น้ำหนักผมลดลงไป 8 กิโลกรัม หลังจากนั้นครับผมลองหยุดดูว่ามนัจะเกิดอาการโยโย่เอฟเฟคไหม ผมหยุดมาตอนนี้ครับ 6 เดือนแล้ว ที่ลดลงไป 8 กิโลกรัมก็ไม่ขึ้นครับ จึงทำให้ผมประทับใจใน SLM และผมก็มีประสบการณ์เยอะมากที่ใช้กับพ่อแม่และภรรยา และพบว่าทุกคนแข็งแรงขึ้นจริงๆครับ ภรรยาผมเป็นโรคภูมิแพ้จนเป็นไวนัสอักเสบ ในฐานะของแพทย์แผนปัจจุบัน ไม่สามรถรักษาได้แล้วจริงๆ จึงต้องผ่าตัดล้างไซนัส แต่หลังจากได้ทาน EXO,UMI เพียง 1 เดือนเท่านั้น อาการดีขึ้นไม่ต้องผ่าตัด ขอให้ความมั่นใจสินค้าของสารอาหารแบบเจลดีจริงๆ', '1,2,4'),
	(30, 1, 'ทพ.พงษกร สงศ์สายตา', 'ทันตแพทย์ จ.มุกดาหาร', 'ประสบการณ์ตรงของผมคือคุณแม่ผมเป็นมะเร็งครับ ระยะที่สามแล้ว ก็รักษาทางการแพทย์แผนปัจจุบัน และผมก็ได้ลองใช้อาหารเสริมควบคู่กันด้วยทำให้คุณแม่มีสุขภาพที่ดีขึ้น อาหารเสริมที่ผมประทับใจก็คือ UMI และ EXO ผมให้แม่ทานทั้ง 2 ตัว ปรากฏว่าโรคมะเร็งของคุณแม่ตอนนี้ก็หายไปแล้ว คือก็รักษาทั้งเคมีบำบัด ฉายแสงรังสี และทานอาหารเสริมควบคู่ไปด้วย ปัจจุบันคุณแม่ท่านสุขภาพแข็งแรงมากครับ ตอนนี้ตรวจไม่พบมะเร็งแล้ว รู้สึกภูมิใจมากที่คุณแม่ได้ทานสารอาหารแบบเจลครับ', '1,2'),
	(31, 1, 'สัตวแพทย์หญิงอิงณภัทร์ ภัคสิริไพศาล', 'เจ้าของโรงพยาบาลสัตว์', 'การทำงานของตัวเองจะทำงานหนักมากเพราะเป็นเจ้าของโรงพยาบาลสัตว์ เปิด 7 วัน ไม่มีวันหยุด และคลุกคลีกับสิ่งที่กระตุ้นให้เราแพ้ง่าย พวกฝุ่นละออง เศษขน ก็จะกระตุ้นให้เราเป็นสิวตลอดเวลารักษาอย่างไรก็ไม่หาย หลังจากรู้จักสารอาหารแบบเจลได้รับประทาน EXO วันละ 2 ซอง สุขภาพผิมพรรณดีขึ้นไม่มีอาการแพ้ ไม่มีสิวขึ้นเลย สุขภาพผิวหน้าดีขึ้นมาก จุดเด่นที่สุดยอดมากๆ สารอาหารแบบเจลอยู่ที่การดูดซึม', '1'),
	(32, 1, 'ภก.วิชัย ประวัติเมือง', 'เจ้าของร้านเภสัช 6 สาขา', 'ตัวผมเองมีปัญหาเรื่องคอเลสตอรอลสูง แต่ไม่อยากทานยา เราเป็นเภสัชเรารู้ผลข้างเคยงของยาดี จึงเลือกทานอาหารเสริม หลังจากทานสารอาหารแบบเจลแล้วสุขภาพดีขึ้น ปัจจุบันเราทำประกันสุขภาพกับสารอาหารแบบเจล เราให้ความสำคัญกับการรักษาที่ต้นเหตุของโรค ไม่ต้องเสียงบประมาณในการจ่ายค่าโรคต่างๆในอนาคต ถ้าเปรียบเทียบสารอาหารแบบเจล ก็เปรียบเสมือ mp3,mp4 ที่เครื่องเล็กนิดเดียวแต่สามารถจุอะไรได้เยอะมากมาย ส่วนสารอาหารแบบเจลซองเล็กนิดเดียวแต่คุณภาพมหาศาล', '0'),
	(33, 1, 'ภญ.ปนิตา ตันติวิท', 'เภสัชกร', 'ปกติทานอาหารเสริมอยู่แล้ว เพราะรู้ว่าอาหารเสริมช่วยในแง่ของการป้องกันปัญหาเกี่ยวกับโรคความเสื่อม หลังจากที่ศึกษาสารอาหารแบบเจลก็พบว่าเป็นนวัตกรรมของอาหารเสริมในเรื่องของการดูดซึม ซึ่งเป็นสารสกัดจากธรรมชาติทั้งหมด และสะดวกในการพกพา อย่างเช่น แทนที่จะทานวิตะมินหลายๆชนิด เราทาน MIN แค่ซองเดียวก็เพียงพอ ตัวเองมีคอเลสตอรอลสูงถึง 274 ได้ทาน HRT แล้วพบว่าระดับคอเลสตอรอลลดลงเหลือเพียง 215 จึงมั่นใจมาก ได้ให้ลูกสาวที่มีอาการภูมิแพ้ทาน UMI ปรากฏว่าอาการภูมิแพ้หายไปเลยค่ะ เมื่อตัวเองทานดี ลูกทานดี ก็มีความรู้สึกอยากแนะนำสิ่งดีๆให้กับเพื่อนบ้าง รู้สึกมั่นใจขึ้นทุกวันกับผลิตภัณฑ์สารอาหารแบบเจลค่ะ', '2,3,7'),
	(34, 1, 'คุณอุดมพร บุญรอด', 'พยาบาลวิชาชีพ 7 ประจำงานอุบัติเหตุ – ฉุกเฉิน โรงพยาบาลชุมชน จ.เชียงราย', 'ประทับใจในผลิตภัณฑ์ SLM ใช้เดือนแรกน้ำหนักลดลงมา 4 กิโลกรัม รอบเอวรอบสะโพกลดไป 7 เซนติเมตร แนะนำให้พี่สาวใช้ลดไป 4 กิโลกรัมเหมือนกัน รอบเอวรอบสะโพกอย่างละ 3 นิ้ว รู้สึกดีใจมากภูมิใจมากที่เราเจอผลิตภัณฑ์ดีๆแบบนี้ ทานง่ายสะดวกดีกว่าผลิตภัณฑ์ลดน้ำหนักอื่นๆที่เราเคยทานมา และได้ให้ลูกสาวทาน UMI จากที่มีอาการภูมิแพ้ กลางคืนคัดจมูก หายใจเสียงดัง ตอนเช้ามีอาการคัดจมูก น้ำมูกไหล หลังจากทาน UMI แล้วอาการก็ดีขึ้นไม่มีอาการอีกเลยค่ะ', '2,3'),
	(35, 1, 'คุณณัฐรดา รวมใหม่', 'นางพยาบาล โรงพยาบาลหัวใจกรุงเทพ', 'พ่อของดิฉันเป็นโรคลิ้นหัวใจรั่ว แต่ยังเป็นชนิดอ่อนยังไม่ถึงขั้นผ่าตัด ปกติทานจะมีอาการเหนื่อยง่าย คอเลสเตอรอลไม่ต่ำกว่า 250 พอได้มารู้จักกับ HRT ก็เลยแนะนำให้พ่อทาน หลังจากทานไปได้ 1 เดือน คอเลสเตอรอลก็ลดลงมาจาก 250 เหลือ 160 ที่สำคัญอาการเหนื่อยของท่านก็ลดลงและทำงานได้ปกติ ขอบคุณ HRT ค่ะ', '7'),
	(36, 1, 'ภก.เกียรติพงษ์ ตรังคานนท์', 'เภสัชกร ', 'การทานอาหารเสริมเป็นศาสตร์หนึ่งของการป้องกันฟื้นฟูร่างกาย ไม่ใช่การรักษา ถ้าเรามีการฟื้นฟูร่างกายและป้องกันทำให้เรามีสุขภาพดีขึ้น ความเสี่ยงต่อการเป็นโรคน้อยลง การรักษาน่าจะเป็นทางเลือกสุดท้าย และเชื่อว่าสารอาหารแบบเจลซึ่งมีนวัตกรรมใหม่ในเรื่องการดูดซึมเป็นทางเลือกที่ดีให้กับผู้คน', '0'),
	(37, 1, 'ภก. วิชภูมิ พิบูลศุภธนา', 'เภสัชกร ', 'ผมทำวิจัยทางด้านยามะเร็ง คนไข้ทั่วไปจะได้รับการฉายรังสี ให้คีโมทำให้ร่างกายถูกกดจากตัวยาต่างๆทำให้ภูมิคุ้มกันต่ำลง คนไข้จะต้องอาศัยภูมิคุ้มกันเพื่อทำให้ร่างกายฟื้นขึ้นมาเองจนกว่าจะสามารถรับยาครั้งต่อไปได้ ผมอยากช่วยคนไข้จึงไปหาข้อมูลในอินเทอร์เน็ต พบสารที่ชื่อว่า “ฟูคอยเดน” จากสาหร่ายใต้ทะเลลึก พบว่าสารตัวนี้สามารถเพิ่มภูมิคุ้มกันได้ จึงไปพบสารอาหารแบบเจลชื่อ UMI และได้มีโอกาสนำ UMI ให้เพื่อนบ้านซึ่งเป็นโรคมะเร็งเม็ดเลือดขาวแบบเฉียบพลัน ให้ทาน UMI 15 ซอง เม็ดเลือดเต็มขึ้นจนหมอแปลกใจ เนื่องจากอยู่ในส่วนของการดูแลคนไข้มะเร็งโดยเฉพาะ ทำให้ตัวเองตระหนักดีว่า การที่จะรอให้เป็นมะเร็งแล้วรักษามันเป็นไปได้ยาก และการตรวจพบเซลล์มะเร็งกว่าจะตรวจพบ ส่วนใหญ่จะไประยะที่ 3 ระยะที่ 4 แล้ว ซึ่งเราไม่สามารถที่จะป้องกันได้ทัน ตัวเองจึงตัดสินใจดูแลสุขภาพตัวเองด้วย EXO และ UMI', '1,2'),
	(38, 1, 'ภก.เกรียงไกร สิริรัตน์ตระกูล', 'เภสัชกร ', 'จากการที่ผมได้ศึกษาเกี่ยวกับผลิตภัณฑ์อาหารเสริม ก็พบว่าไม่เพียงแต่เป็นการเสริมสร้างสุขภาพเท่านั้น แต่เป็นคำตอบหนึ่งสำหรับผู้ที่มีปัญหาสุขภาพได้อีกทางหนึ่งด้วย ในส่วนสารอาหารแบบเจลนี้ ผมเปิดใจจาก SLM เนื่องจากน้องสาวผมทาน SLM แล้วสามารถลดน้ำหนักลงได้ถึง 5 กิโลกรัมใน 1 เดือน ผมเองจึงเริ่มทาน EXO เพื่อเสริมสร้างภูมิคุ้มกัน และป้องกันมะเร็งในอนาคต เพราะ EXO มีสารต้านอนุมูลอิสระและวิตะมินเป็นจำนวนมาก แต่เมื่อทานแล้วพบว่า ผิวพรรณผมสดใสขึ้น ตื่นนอนง่าย และรู้สึกว่าสุขภาพดีขึ้น นับวันผมยิ่งมั่นใจในผลิตภัณฑ์มากขึ้นครับ ', '1,4'),
	(39, 1, 'คุณบุญสืบ พันธ์ประเสริฐ', 'นักเทคนิคการแพทย์', 'ผมสนใจในเรื่องของการแพทย์ทางเลือก มีโอกาสได้ศึกษาด้านอาหารเสริมและธรรมชาติบำบัด จนได้พบกับสารอารหารแบบเจลซึ่งเป็นนวัตกรรมใหม่ ที่ออกแบบมาเพื่อช่วยเหลือมนุษย์ในยุคนี้จริงๆ การที่จะรับประทานอาหารให้ครบ 5 หมู่หรือออกกำลังกายให้ครบทุกวันเป็นเรื่องที่ทำกันได้น้อยมาก อาหารเสริมจึงเข้ามามีบทบาทและผมก็ประทับใจในสารอาหารแบบเจล ซึ่งเป็นนวัตกรรมใหม่ในรูปแบบ gel ทานสะดวกให้ประสิทธิภาพสูงมาก อาชีพของผมต้องทำงานกลางคืนด้วย ก็เลยทำให้ค่อนข้างจะมีปัญหาเกี่ยวกับการง่วงนอน ผมก็ได้ OHM ช่วยให้เวลาช่วงบ่ายสามารถทำงานได้อย่างมีสามธิและมีประสิทธิภาพมากขึ้น OHM ช่วยให้ผมไม่ง่วงและในขณะเดียวกันก็ทำให้สดชื่นตลอดเวลาด้วย ตอนนี้ผทก็ทาน OHM กับ EXO อยู่ ก็ทำให้การทำงานนั้นดีขึ้นและสดชื่นขึ้น ขอบคุณครับ', '1,5'),
	(40, 1, 'ภก.ปัทมา ไอยาราคม', 'เภสัชกร ', 'พี่ที่ทำงานมีเกล็ดเลือดที่ต่ำมาก ประมาณ 40,000-50,000 มีอาการเครียดตลอดกลัวเป็นมะเร็ง หลังจากได้เข้ามาศึกษาพบว่า UMI สามารถช่วยได้ จึงแนะนำให้ทาน ทานไป 3 อาทิตย์ จากเม็ดเลือด 40,000-50,000 ขึ้นไป 2 แสน', '2'),
	(41, 1, 'ภญ.ปิยะวรรณ ใบโสภณ', 'เภสัชกร ', 'เริ่มมีสิวมาตั้งแต่เรียนมหาวิทยาลัยปี 2 ค่ะ อาจจะเป็นเพราะเรียนหนัก เครียด ฝุ่น ฮอร์โมน หรืออาจจะทุกอย่างรวมกัน ทำให้สิวมีลักษณะอักเสบ บวมแดงและเม็ดใหญ่จำนวนมากบริเวณแก้ม และเป็นหนักขึ้นเรื่อยๆ เป็นเยอะมาก แทบจะไม่เว้นรูขุมขนเลย เซ็งมากเลยค่ะ พราะเวลาที่เราคุยกับคนอื่นแล้วเค้าไม่มองตาเรา (มองแต่สิวเรา) แต่เนื่องจากตัวเองอยู่ในวงการเภสัชกร ทำให้มีความรู้ความเข้าใจเกี่ยวกับเรื่องยา จัดยารักษาสิวให้ตัวเองทานมาตลอด ช่วงที่กินยา ก็รู้สึกดีนะค่ะ หน้าใสเลยแหละค่ะ แต่พอหยุดยาสิวก็ขึ้นมาอีก ก็เลยกินยอีก หน้าก็ใส แล้วก็ลองหยุดยา สิวก็ขึ้นมาอีก เป็นอย่างนี้ประมาณ 4 ปีค่ะ จิตตกมาก ทำอะไรก็ไม่มั่นใจ แม้จะเรียนจบมาและทำงานแล้ว ก็ยังคงมีสิวอยู่ อยากกลับไปกินยาอีกนะค่ะ แต่ก็กลัวว่าถ้ากินไปนานๆแล้ว ตับอาจจะมีปัญหา ช่วงหลังๆก็เลยปล่อยไม่ได้รักษาอะไรและหวังว่าเดี๋ยวสิงก็คงหายไปเอง แต่มันกลับไม่หาย เป็นมากขึ้นทุกๆวัน ถ้านอนดึก 1 คืน สิวจะขึ้นมา 2-3 เม็ด ถ้านอนดึกหลายๆคืนติดกัน สิวมันจะขึ้นเต็มหน้าเลยค่ะ สิวมันซ้อนๆกันเต็มไปหมด ยอมรับว่าช่วงนั้นต้องหลบกระจก เพราะว่ากลัวหน้าตัวเองในกระจกค่ะ พอดีพี่ชายได้แนะนำให้มารู้จักกับสารอาหารแบบเจล ก็เห็นหลายคนบอกว่า EXO กินแล้วหน้าใส และหลายคนสิวก็หายก็เลยลองกินดูค่ะ ได้มีโอกาสปรึกษากับคุณหมอชนินทร์ ท่านแนะนำว่าควรทานต่อเนื่องสัก 3-4 เดือน เพราะเป็นอาหารเสริมไม่ใช่ยารักษาโรค ก็เลยทานต่อเนื่องมาวันละ 1 ซองโดยตลอดค่ะ และยิ่งไปกว่านั้น ช่วงที่บำรุงผิวด้วย Ageless Skin Care เห็นผลไวมากค่ะ สิวอักเสบเริ่มลดลง รอยแผลเป็นจากสิวก็ค่อนข้างตื้นขึ้น ตอนนี้ทานมา 6 เดือนแล้วไม่มีสิวเลยค่ะ ถือเป็นระยะเวลาสั้นมากนะค่ะ เพราะว่าตุ๊กตาเคยรักษาด้วยยา กว่าหน้าจะใสต้องใช้เวลา 2-3 เดือน พอหน้าใสแล้วหากต้องการหยุดยาก็ต้องช้เวลา 2-3 เดือน ในการลดยา เวลาที่ใช้ลดสิวพอๆกัน แถมยังปลอดภัย และสบายใจมากกว่า เพราะ EXO เป็นสารสกัดจากผลไม้เข้มข้นที่เป็นธรรมชาติล้วนๆ 100% ขอขอบคุณ EXO คุณธีรพงศ์ โหมดหิรัญ และคุณสกล ในโสภณ ที่แนะนำให้รู้จักกับผลิตภัณฑ์ที่มหัศจรรย์แบบนี้', '1'),
	(42, 1, 'คุณนัทธมน พันธุ์มณี (ต้อม)', 'นักแสดง', 'ต้องเริ่มทานผลิตภัณฑ์สารอาหารแบบเจลก็เพราะว่าพี่เป้แนะนำ เห็นพี่เป้ในกองถ่าย ก็ประมาณว่า เอ...she ทานอะไร ดูมันทานง่ายมากและดูน่ารักดี ก็เลยถามพี่เขา พี่เป้ก็เลยแนะนำให้ต้อมทาน ก็จะมี EXO ทานง่ายมากเลยค่ะ หวานๆเปรี้ยวๆ มีแชร์กับลูกบ้างนิดหน่อย ต้อมกับลูกมีปัญหาเรื่องภูมิแพ้ วีวี่เกิดปัญหานี้ตั้งแต่เด็ก เลยคิดว่าถ้าเขาลองทานด้วยก็จะดี ก็เลยแบ่งกันทาน แต่ลูกก็ทานแค่ครึ่งเดียว ส่วนของต้อมเอง เขาบอกว่าช่วยทั้งภูมิแพ้ ทำให้หน้าใส ต้อมทานมาระยะหนึ่งมันก็โอเคอยู่ค่ะ อีกอย่างที่ต้อมค่อนข้างที่จะกลัวเหมือนกัน เพราะว่าบ้านต้อมจะมีกรรมพันธุ์ คือ มะเร็ง ในครอบครัวก็ตัดไปแล้ว 3 คน ก็เสียวเหมือนกันว่ามันจะเกิดขึ้นกับเราหรือเปล่า แต่พี่เป้แนะนำว่า UMI เป็นตัวที่ต้านการเป็นมะเร็งได้ เรื่องภูมิแพ้ก็โอเค ก็เลยรู้สึกว่ามันน่าจะโอเคกับตัวเรา ต้อมเป็นข้อล็อกบ่อยมาก UMI นี้จะช่วยเรื่องข้ออักเสบด้วย ต้อมมีความรู้สึกว่ากินมาระยะหนึ่งแล้วมันดีขึ้น สำหรับตัวต้อมเองจริงๆค่ะ', '1'),
	(43, 1, 'คุณวรนันท์ จันทรรัศมี (จุ้มจิ้ม)', 'นักแสดง', 'จุ้มจิ้มมีโอกาสได้ทดลองใช้ผลิตภัณฑ์สารอาหารแบบเจล มาเป็นระยะเวลาพอสมควรแล้วค่ะ เพื่อนคู่ใจที่ทานกันมาตลอดคือ EXO เพราะอันดับแรกชอบรสชาติมาก ถ้าแช่เย็นยิ่งอร่อยมากค่ะ ปกติตื่นเช้ามา ก่อนทานอาหารเช้าท้องว่างๆก็จะหยิบ EXO ขึ้นมาทานก่อนเลย รู้สึกว่าเป็นการเริ่มต้นวันที่สดชื่น และพกง่ายทานง่ายมาก ถ้ารีบๆไปกองถ่ายทำงาน อาหารเช้าไม่ทันก็หยิบติดกระเป๋าไว้ รถติดๆก็ฉีกซองทาน มันก็ง่ายค่ะ ที่สำคัญได้รับการแนะนำว่าคุณประโยชน์ตรงกับความต้องการของจุ้มจิ้มและผู้หญิงอีกหลายๆคน คือ เรื่องผิวพรรณ ที่ผู้หญิง อย่างไรก็ยอมไม่ได้ต้องผิวสวยหน้าใสไว้ก่อน ยิ่งจุ้มจิ้มต้องทำงานในวงการบันเทิง ยิ่งต้องดูแลผิวพรรณเป็นพิเศษ และทราบว่า EXO ช่วยลดการอักเสบของสิว ลดรอยดำ ลอดรอยแดง ลดจุดด่างดำด้วย ก็เลยยิ่งตรงใจเข้าไปกันใหญ่ค่ะ นอกจากนั้นปกติจะเป็นคนที่ปวดท้องประจำเดือนมากๆ ตั้งแต่เด็กเวลามีรอบเดือน เจอปัญหาที่ปวดมากจนทำอะไรไม่ได้ พอได้ทดลองทานมาเป็นระยะเวลาหนึ่งแล้ว บวกกับออกกำลังกายไปด้วย อาการปวดก็ค่อยๆลดลง จนตอนนี้ไม่ค่อยมีปัญหาแล้ว ถึงประจำเดือนมาก็ทำกิจกรรมและทำงานได้ตามปกติ อีกเรื่องจุ้มจิ้มเป็นคนท้องผูกตลอดเวลา ทานแล้วรู้สึกว่าอาการท้องผูกลดลง ช่วยเรื่องระบบการขับถ่ายของเราได้ดี เลยคิดว่า คงต้องทานกันต่อไปเรื่อยๆค่ะ', '1'),
	(44, 1, 'คุณสมเจตน์ ทองทา', 'นักเรียน กรุงเทพฯ', 'ประทับใจ EXO เนื่องจากผมเป็นไซนัสเรื้อรังมานาน 10 กว่าปีแล้ว แล้วก็ทานยาจีนต้มมาตลอด ขมมากแต่ก็ต้องทนทาน จนผมได้รู้จักกับตัว EXO ประมาณปลายปีที่แล้ว แต่ก่อนมีน้ำมูกไหลทุกวัน หายใจไม่ออก ทรมานมาก พอทานได้ 2 อาทิตย์ก็เห็นผลชัดเจนมาก อาการเหล่านั้นก็หายไป ก็เลยทานติดต่อมาอีก 2 เดือน ปรากฏว่าไม่เป็นอีกเลยจนตอนนี้ผมก็ทานมาตลอด ทุกครั้งที่อากาศเปลี่ยนก็ไม่มีอาการอะไรเกิดขึ้น ก็อยากจะบอกสำหรับคนที่มีปัญหาเรื่องภูมิแพ้ไซนัสว่า EXO ช่วยท่านได้แน่นอนครับ', '1'),
	(45, 1, 'คุณศิราเมษฐ์ รัตนศานติพงษ์', 'อดีตวิศวกรก่อสร้าง กรุงเทพฯ', 'ในอดีตที่ผมเป็นวิศวกรก่อสร้าง ผมทำงานหนักและเยอะมากในระหว่างวัน แล้วก็ต้องทำล่วงเวลาทุกคืน ที่ทำงานของผมจะเป็นไซด์งานแล้วก็เต็มไปด้วยฝุ่นละออง ผมก็จะรู้สึกเพลียแล้วก็เจ็บคอมากๆ ทุกๆเช้าที่ตื่นนอน ผมจะรู้สึกเจ็บคอแล้วก็เพลีย พอผมมารู้จักผลิตภัณฑ์สารอาหารแบบเจล ที่ชื่อว่า EXO หลังจากที่ผมทาน ทุกๆเช้าที่ผมตื่นมา ผมกลับรู้สึกปลอดโปร่ง รู้สึกสดชื่น อาการเจ็บคอที่ผมเคยเป็นมันก็หายไป ตอนนี้ผมทาน EXO ก่อนนอนทุกคืนครับ ทาน EXO แล้วไม่เพียงแต่ก่อให้เกิดรายได้ ยังทำให้สุขภาพดีด้วยครับ', '1'),
	(46, 1, 'คุณนวพร อภิรติวงศ์พิศาล', 'ตัวแทนประกันชีวิต', 'โดยส่วนตัวประทับใจ EXO มากๆ เพราะว่าเมื่อก่อนนี้จะเป็นคนที่มีปัญหาสิวอักเสบค่อนข้างจะเยอะพอสมควร แล้วก็ผมมันด้วย แต่หลังจากที่เจอสารอาหารแบบเจลแล้ว ได้ทดลองผลิตภัณฑ์ EXO ประมาณ 1-2 เดือน เห็นผลชัดเจนเลยว่าสิวอักเสบหายไปหมดเลย แล้วก็อาการผมมันก็ดีขึ้นเยอะมาก หลังจากนั้นเพื่อนก็เริ่มทัก และก็ทำให้เรามีรายได้จากตรงนี้ด้วยค่ะ', '1'),
	(47, 1, 'คุณเอกพฤกษ์ วิสิฐศักดาพงศ์', 'อดีตนักวิจัยและพัฒนาซอต์ฟแวร์ กรุงเทพฯ', 'ประทับใจ EXO ครับ คือผมจะมีปัญหาท้องผูกมาตั้งแต่เด็กๆ ประมาณ 20 กว่าปีแล้ว ผมได้รับคำแนะนำว่าให้ทานผัก ทานน้ำให้มากขึ้น ปรากฏว่าอาการท้องผูกก็ยังไม่ดีขึ้น อาการท้องผูกของผมเป็นหนักมาก คือ 5-7 วันถึงจะถ่าย 1 ครั้ง หลังจากใช้ EXO ในวันแรกอย่างแรกที่ประทับใจคือ  EXO มีรสชาติดีมากทานง่าย วันแรก ผมสามารถถ่ายได้ด้วยตนเอง 3-4 ครั้ง จนปัจจุบันผมใช้ EXO มาประมาณ 4 เดือน กรขับถ่ายของผมเรียกได้ว่าเกือบจะเป็นปกติเลย', '1'),
	(48, 1, 'คุณสุภณัฎฐ์ ธีรภัทรสกุล', 'วิศวกร กรุงเทพฯ', 'จากลักษณะงานที่ผมทำ ผมต้องสัมผัสกับเครื่องจักรตลอดเวลา สิ่งเหล่านี้จะมีพวกคราบน้ำมัน ไอน้ำมันติดอยู่ตลอดเวลา ทำให้ผมมีอาการเหมือนเป็นภูมิแพ้ คือจะมีน้ำมูกใสๆออกมาตอนเช้าตลอด หลังจากที่ผมได้ลองทาน EXO ตอนนี้ผมไม่มีอาการเกี่ยวกับโรคภูมิแพ้อีก ทำให้ผมทำงานได้อย่างเป็นปกติครับ', '1'),
	(49, 1, 'คุณอัจจิมา จังเกษม', 'นักศึกษาปี 3 หมาวิทยาลัยกรุงเทพ', 'ประทับใจตัว EXO คือ เมื่อก่อนเป็นโรคเกี่ยวกับทางเดินหายใจทั้งหมด เป็นภูมิแพ้ หอบหืด ไซนัส หลอดลมอักเสบ ตอนนั้นคือใช้ยาเยอะมากทั้งยาพ่น ยาหยอด เยอะมากค่ะและต้องพกยาประจำ แต่เดี๋ยวนี้สิ่งที่ได้รับจาก EXO คือ มันบรรจุเพียงแค่ซองเดียว โดยที่เราไม่ต้องพกยาเป็นสิบๆอย่าง ซึ่งมันสะดวกมากค่ะ ขอบคุณค่ะ', '1'),
	(50, 1, 'คุณวัชรเศรษฐ พวงทอง', 'ธุรกิจส่งออกรองเท้าไม้', 'ผลิตภัณฑ์ที่ประทับใจ คือ EXO ด้วยงานที่ผมทำอยู่ ผมต้องเผชิญกับฝุ่นละออง แล้วก็ทำงานหนัก นอนดึก ทำให้หน้ามีสิวมาก แต่ก่อนผมก็เข้าคลินิก พอทานยาสิวก็หายไป แต่พอหยุดทานสิวก็กลับมาอีก พอหลังจากผมมาพบ EXO ผมลองทานในเดือนแรก ผมก็รู้สึกหน้ามีผิวที่ดีขึ้น สิวน้อยลง พอทานต่อเนื่อ 2 เดือน สิวก็หายไป ทำให้ผมมีความสุขมากกับ EXO ครับ ขอบคุณ EXO มากครับ', '1'),
	(51, 1, 'คุณรัล สวรลเศวตบุศ', 'อดีตพนักงานประชาสัมพันธ์ท่านอากาศยานกรุงเทพฯ', 'ที่ประทับใจสุดคือ EXO กับ UMI ค่ะ คือว่าตอนที่ทำงานประจำต้องำงานเป็นกะ นอนดึก สิวก็จะขึ้นเยอะมาก แล้วก็จะทิ้งรอยไว้ พอทาน EXO ไปได้สักระยะหนึ่งรู้สึกเลยว่ารอยดำลดลง แล้วก็ช่วยในเรื่องริ้วรอยด้วย อีกตัวที่ชอบคือ UMI ค่ะ โดยปกติแล้วจะเป็นคนไม่ดื่มเหล้า ต่พอไปดื่มก็จะมีอาการเมาค้าง UMI จะเป็นตัวช่วยได้ดีมากสำหรับคนที่จำเป็นต้องดื่มเหล้าและต้องทำงานในวันรุ่งขึ้น UMI เป็นตัวช่วยได้ดีที่สุดค่ะ ขอบคุณค่ะ', '1'),
	(52, 1, 'คุณสัญญา เทพเพียร', 'เจ้าหน้าที่แผนกห้องผ่าตัด โรงพยาบาลรัฐบาล กรุงเทพฯ', 'สารอาหารแบบเจลผมประทับใจหลายตัว แล้วก็มีคนรอบข้างใช้ได้ผลมากมาย แต่ที่ผมเห็นผลมากที่สุดคือ EXO ครับ ผมจะใช้ทาหน้าก่อนนอนทุกวัน ผมทาแล้วทิ้งไว้จนถึงเช้า แต่ก่อนหน้าผมจะเป็นสิวแล้วก็เป็นแผลเป็นมาก บางคนทักว่าไปโดนของมาหรอ ผมใช้เพียง 1 อาทิตย์ หน้าตาผมดีขึ้น รู้สึกว่านุ่มขึ้น ไม่ต้องไปใช้ตัวอื่นเลยครับขอบคุณ EXO ครับ', '1'),
	(53, 1, 'คุณธันย์จิรา อมรมงคลวัฒน์', 'นักทดสอบระบบคอมพิวเตอร์', 'คุณแม่อายุ 64 ปีแล้วมีอาการวัยทอง คุณหมอก็จัดยาฮอร์โมนให้ทานอยู่ตลอด คุณแม่ทานไปทานไปนานๆเข้า คุณหมอก็บอกว่าต้องหยุดทานนะ เพราะอาจจะมีอาการเป็นมะเร็งได้ พอเปิ้ลได้รู้จักกับสารอาหารแบบเจลก็เลยแนะนำ EXO ให้ทาน คุณแม่บอกว่าตอนแรกที่เคยนอนหลังสี่ทุ่มไปแล้ว จะมีอาการปวดหัวเวลาตื่นในตอนเช้า ตอนนี้ตื่นขึ้นมาใส่บาตรตอนเช้าได้ปกติ ระบบขับถ่ายก็ปกติ ผิวพรรณก็สดใสขึ้น ขอบคุณ EXO ค่ะ', '1'),
	(54, 1, 'คุณกษิรา วิชัยดิษฐ์', 'ประกอบธุรกิจส่วนตัว จ.สุราษฎร์ธานี', 'ก่อนหน้าที่จะมาประกอบธุรกิจส่วนตัว เป้นพนักงานของบริษัทเอไอเอส ซึ่งจะต้องใช้โทรศัพท์นานมาก ประมาณวันละ 8-9 ชั่วโมง ปีแรกจะไม่มีอาการ พอปีที่สองปรากฏว่าเจ็บเสียวๆบนสมอง เลยไปพบหมอกพบว่าเป็นเส้นประสาทอักเสบ ซึ่งมันทรมานมาก ตอนกลางคืนจะนอนไม่หลับ มีน้ำตาไหลตลอดและต้องทานยาเป็นประจำ หลังจากที่ได้รู้จัก EXO พอทานได้สักประมาณ 1 เดือน ก็รู้สึกว่าอาการที่ปวดเสียวบนประสาท อาการนอนไม่หลับก็หายเป็นปลิดทิ้งเลยค่ะ ขอบคุณ EXO ค่ะ', '1'),
	(55, 1, 'คุณรัชฎา โสทอง', 'นักศึกษาวิศวกรรมศาสตร์', 'ตัวเองเป็นภูมิแพ้ ผิวแห้ง ประจำเดือนก็มาไม่ปกติสองสามเดือนมาครั้งหนึ่ง พอใช้ EXO แล้วก็ช่วยได้ดีมากผิวพรรณก็ชุ่มชื้นขึ้น ในเรื่องของภูมิแพ้ก็ดีขึ้น พกพาสะดวก ทานง่าย ก็ขอขอบคุณ EXO มากๆ ค่ะ', '1'),
	(56, 1, 'คุณกนกวรรณ วงศ์ศักดา', 'เจ้าของกิจการร้านเสริมสวย', 'ผลิตภัณฑ์ที่ประทับใจคือ EXO ค่ะ เพราะปกติแล้วพลอยเป็นโรคภูมิแพ้เป็นหนักมาก คือตื่นมาแล้วก็จะหายใจไม่ออก ตอนนอนก็น้ำตาไหล ต้องทานยาแอคทริเฟรดทุกวัน วันละ 2-3 เม็ด แต่พอได้มาทาน EXO มันก็ไม่ได้หายเลยแต่ว่าอาการดีขึ้นไม่ต้องทานยาก็ได้ ขอบคุณค่ะ', '1'),
	(57, 1, 'คุณจักริน เขตคาม', 'พนักงานฝ่ายการเงิน', 'ผลิตภัณฑ์ที่ชอบคือ EXO  เพราะ EXO   ทำให้หน้าใส ปกติผมจะเป็นภูมิแพ้ ตอนกลางคืนก็จะไอ พอทานได้ซักพักนึงรู้สึกว่าอาการภูมิแพ้จะหายไป ขอบคุณมากครับ', '1'),
	(58, 1, 'คุณนปภัทร หอละเอียด', 'ธุรกิจรีสอร์ท จ.กาญจนบุรี, ตัวแทนจำหน่าย ชุดปรับสรีระ', 'มาเจอผลิตภัณฑ์สารอาหารแบบเจล จกการบอกเล่าของผู้ใหญ่ที่เคารพท่านหนึ่งค่ะ สำหรับตัวเองแล้วประทับใจในตัว EXO กับ UMI เพราะว่าเป็นภูมิแพ้มาเกือบ 10 ปี แล้วก็จากปกติที่ไอทุกวันทุกคืนก่อนนอนจนกระทั่งตอนเช้าจะนอนได้น้อยมาก พอทาน EXO กับ UMI ทำให้เราไม่ไอเลยแล้วก็หลับสนิทมากขึ้น ขอบคุณค่ะ', '1'),
	(59, 1, 'คุณสมพร ต้อนรับ', 'พนักงานประจำ', 'เป็นภูมิแพ้ค่ะมีอาการป่วยตลอดเป็นอย่างหนัก เมื่อก่อนนี้ต้องหายใจทางปาก เวลานอนก็ต้องเอาผ้าปิดปากแล้วเอากระดาษอุดจมูกไว้ เพราะเป็นหวัดหนัก ก็ทานยาหาหมอทรมานมาตลอดก็ไม่หายพอมารู้จักสารอาหารแบบเจลก็ได้ทานเมื่อเดือนสิงหาคมที่ผ่านมาก็ดีขึ้น ตอนนี้ก็ไม่เป็นหวัดอีก ตัวที่ประทับใจที่สุด ก็คือตัว EXO ทานแล้วรู้สึกว่าภูมิแพ้ไม่เป็นแล้ว สามารถไปวิ่งออกกำลังกายได้ทุกวัน เพราะเมื่อก่อนนี้ไม่สามารถวิ่งออกกำลังกายได้ เพราะจะมีอาการเหนื่อยแล้วหายใจไม่ออก แต่ตอนนี้ไม่เป็นแล้ว สามารถวิ่งออกกำลังกายได้วันละ 5 กิโลเมตรทุกเช้าเลยค่ะ ขอบคุณค่ะ', '1'),
	(60, 1, 'คุณอรณัท ชื่นชมภู', 'อดีตเจ้าของกิจการ', 'ผลิตภัณฑ์ที่ประทับใจก็คือตัว EXO ซึ่งโดยตัวเองและครอบครัวได้ทาน EXO ไปแค่ 2-3 เดือนที่ผ่านมาในช่วงที่อากาศเปลี่ยน บ้านเราไม่มีใครเป็นไข้เลยสักคนเดียว และอีกตัวหนึ่งก็คือ Ageless ด้วยวัยของเราที่ย่างเข้าสู่วัย 40 อัพแล้ว ที่ผ่านมาริ้วรอย จุดด่างดำที่เกิดขึ้น พอได้ใช้ Ageless เพียงแค่ 2 เดือน จากใบหน้าที่เคยแย่ๆก็กลับดีขึ้น ก็ขอบคุณค่ะ', '1'),
	(61, 1, 'คุณอาทิตยา พรประดับวงศ์', 'พนักงานประจำ', 'ผลิตภัณฑ์ที่ชื่นชอบคือ EXO กับ SLM ทำให้ดิฉันลดน้ำหนักไปได้ 3 กิโลกรัม ส่วน EXO ก็ตอนที่ดิฉันมีประจำเดือนแล้วก็ปวดหัวมาก พอทาน EXO ไปได้ซองนึงแล้วอาการดีขึ้นค่ะ', '1'),
	(62, 1, 'คุณธันยนันท์ พงษ์ประชาชื่น', '', 'ผลิตภัณฑ์ที่ชอบคือ EXO ค่ะ เพราะทานแล้วทำให้หน้าใสภายใน 10 วัน บริเวณที่เป็นจุดด่างดำก็ค่อยๆ จางลง ขอบคุณ EXO ค่ะ', '1'),
	(63, 1, 'คุณสมนึก งามเจริญจิต', 'เจ้าของบริษัทเคมีเกษตร จ.พิษณุโลก', 'ประทับใจสารอาหารแบบเจล จากการที่มีปัญหาสุขภาพตอนอายุ 30 ปี เลยไปพบคุณหมอ หมอบอกว่าเป็นโรคแพ้นิกเกิล คือไม่สามารถที่จะใส่เข็มขัดหรือนาฬิกาได้ มิฉะนั้นจะเกิดอาการแพ้แล้วเกิดอาการพุพอง ไปปรึกษาหมอหลายๆท่านก็บอกว่าเป็นโรคผิวหนัง ไม่มีทางรักษาต้องหลีกเลี่ยง ก็เกิดปัญหามานาน จนมาวันหนึ่งได้รู้จักผลิตภัณฑ์สารอาหารแบบเจล แล้วก็ใช้ผลิตภัณฑ์ตัว EXO หลังจากที่ทานไปได้สักพักหนึ่ง อาการที่แพ้นิกเกิลกลับหายไปอย่างน่าอัศจรรย์ ก็เลยไปแนะนำต่อคนในบ้านที่เป็นอาการค้ายๆกัน ก็เห็นผลทั้งครอบครัว ตื่นเต้นกับ EXO มากและก็ขอขอบคุณ EXO มากครับ', '1'),
	(64, 1, 'ภญ.ปิยะวรรณ ใบโสภณ', 'เภสัชกร', 'คุณพ่ออายุ 62 ปีค่ะ มีโรคประจำตัวที่ฮิตมากๆค่ะ คือ เบาหวาน ทานยามา 20 ปี แล้วต้องเพิ่มยามากขึ้นเรื่อยๆ ช่วง 2-3 ปีหลังมานี้พ่อต้องทานยาแบบ max dose (คือว่าถ้าทานยามากกว่านี้ จะไม่เพิ่มประสิทธิภาพในการรักษา แต่จะเป็นผลเสียต่อร่างกาย โดยเฉพาะตับและไต) คุณหมอนัดตรวจระดับน้ำตาลของคุณพ่อทุก 1 เดือน เนื่องจากยังคุมระดับน้ำตาลได้ไม่ค่อยดี ทุกครั้งที่คุณพ่อไปโรงพยาบาลเขาจะงดน้ำ / อาหาร ตั้งแต่เที่ยงคืน แล้วไปตรวจ 8 โมงเช้า ระดับน้ำตาลของคุณพ่อไม่เคยต่ำกว่า 200 เลย (ค่าปกติ คือ น้อยกว่า 110 mg% หรือ ถ้าได้น้อยกว่า 130 คุณหมอก็พอใจแล้ว) คุณหมอต้องเพิ่มยากินให้คุณพ่อจน max dose แล้วแต่น้ำตาลก็ยังสูงอยู่มาก พ่อมีความรู้สึกท้อมากๆ ลองคิดดูนะคะ คนที่ควบคุมอาหาร ข้าวทานเยอะไม่ได้ ผลไม้ก็ทานได้แค่ 2-3 ชิ้น น้ำเต้าหู้ กาแฟก็ไม่ใส่น้ำตาล ขนมหวานไม่ต้องพูดถึง พ่อไม่ทานเลยค่ะ แล้วน้ำตาลยังไม่ลงอีก พ่อบอกว่า “มีเงินแต่ทานอะไรไม่ได้ รู้สึกได้เลยว่าคุณภาพชีวิตของคุณพ่อแย่มาก”  3 เดือนก่อนหน้านี้ คุณหมอจึงตัดสินใจเปลี่ยนยาจากยากินส่วนหนึ่งมาเป็นยาฉีดอินซูลิน เพราะจากการที่ตุณพ่อควบคุมน้ำตาลไม่ได้ อายุก็เริ่มเยอะแล้ว ประกอบกับทานยามาตอนนี้การทำงานของไตไม่ค่อยดีเท่าไหร่ ใช้ยาฉีดไปประมาณ 2 เดือนแล้ว แต่น้ำตาลยังไม่เคยต่ำกว่า 200 เหมือนเดิม ก็เลยให้คุณพ่อลองทาน UMI ทานวันละ 1 ซอง ทานไปประมาณ 2 อาทิตย์ ก็ลองเจาะน้ำตาลดู ปรากฎว่าได้ 176 คุณพ่อตื่นเต้นมากค่ะ เค้ามีกำลังใจและทาน UMI มาเรื่อยๆ คุณพ่อทานอย่างต่อเนื่องและทานถูกวิธี คือ ทานตอนท้องว่าง หลังจากนั้นก็จะวัดระดับน้ำตาลของพ่อเรื่อยๆค่ะ ทุกๆ 3-4 วัน มันลดลงมาเรื่อยๆ เป็นตัวเลขดังนี้ค่ะ 176 146 116 110 ผ่านมาประมาณ 1 เดือนแล้ว ก็ถึงกำหนดที่คุณหมอนัดพอดี (22/08/2551) ได้ 54 ตื่นเต้นค่ะ และแอบคิดว่าเครื่องเสีย แต่หมอก็บอกว่าวัดมาหลายคนแล้วมันก็ปกติดีนี่นา เครื่องไม่เสียหรอก หมอจึงสั่งลดยาฉีดอินซูลิน พ่อบอกว่า ถ้าต้องกิน UMI วันละ 2 ซอง แล้วไม่ต้องฉีดอินซูลิน พ่อก็ยอม เพราะ UMI อร่อย สิ่งที่ปลื้มที่สุดคือ คุณพ่อมีความสุข ขอบคุณ UMI ค่ะ', '2'),
	(65, 1, 'คุณธนันชัย ชื่นตา', 'เจ้าของสถานีวิทยุมาบตาพุด (DJ)', 'ที่ประทับใจก็คือ UMI ครับ ผมจัดรายการตั้งแต่สี่ทุ่มถึงเที่ยงคืนทุกวัน จัดไปจนถึงประมาณห้าทุ่ม เสียงก็จะหายแล้วก็เจ็บคอ หลังจากนั้นผมมาทาน UMI จากเพื่อนแนะนำมา อาการเสียงหายนั้นก็ไม่มีเลย ขอบคุณ UMI ครับ', '2'),
	(66, 1, 'คุณประภัสนันท์ จารุพัฒน์วรกุล', 'ครูสอนภาษาญี่ปุ่น จ.นครราชสีมา', 'มีพี่เภสัชกรแนะนำให้ทาน UMI คู่กับ EXO ดิฉันเป็นภูมิแพ้ แล้วตอนนี้ก็เป็นไทรอยด์อยู่ คือช่วงที่เป็นทรอยด์ นอนไม่ค่อยได้ค่ะ นอนแป๊ปเดียวตื่น แป๊ปเดียวตื่น เหมือนกับใช้พลังงานอยู่ตลอด ทานยาแล้วก็ไม่ได้ผล อาจจะเป็นเพราะกระเพาะเราไม่ดี จึงมาทาน สารอาหารแบบเจล ทานไปประมาณ 10 ซอง ปรากฎว่านอนหลับสนิททั้งคืน ไทรอยด์ที่เป็นก็ยุบลง อาการภูมิแพ้ที่เคยป่วยเป็นไข้ทุกวันๆก็หายไป ขอบคุณ UMI และ EXO ค่ะ', '1,2'),
	(67, 1, 'คุณอาจบดิทร์ อิทสุวรรณ', 'พนักงานบริษัท จ.ตราด', 'เรื่องราวของผมเกิดขึ้นเมื่อประมาณ 2-3 ปีที่แล้ว คือ น้ำตาลในกระแสเลือดสูง คนจะกลัวมากในเรื่องของเบาหวาน ทำให้ผมต้องกินยา 3-4 เม็ด เป็นระยะเวลา 2 ปีครึ่ง พอผมได้รู้จักตัว UMI ผมก็ได้ลองใช้ เดือนแรกผมกิน UMI ควบคู่กับยา แล้วก้อไปตรวจระดับน้ำตาลในเลือด ก็เริ่มลดลงมาจากปกติที่ 140 ลงมาเหลือประมาณ 122 และผมก็ตัดสินใจเลิกทานยาวันละ 4 เม็ด ทาน UMI วันละ 1 ซอง ผ่านมา 5 เดือน ระดับน้ำตาลอยู่ที่ 117-118 UMI ทำให้ชีวิตผมเปลี่ยนไป ทุกคนที่เป็นโรคเบาหวานมาลองทาน UMI ดูซิครับ แล้วคุณจะรู้ว่าสิ่งที่คุณกลัวจะไม่มีอีกต่อไปเพราะ UMI ช่วยคุณได้ครับ ขอบคุณ UMI', '2'),
	(68, 1, 'คุณพศุตม์ วรภิสิทธิ์', 'เจ้าของร้านขายยา และช่างภาพอิสระ จ.สงขลา', 'ประทับใจในเรื่องของลูกชายเพื่อนท่านนึง เรียนอยู่ชั้นประถม ซึ่งมีปัญหาเกี่ยวกับการขับถ่าย ผมจึงแนะนำ UMI ให้เค้า เค้าก็เลยให้ลูกชายลองทานดู มันทำให้การขับถ่ายลูกชายเค้าดีขึ้น ซึ่งก่อนหน้านี้เค้าก็พยายามหาอะไรหลายๆอย่างมาให้ลูกหลาน ซึ่งปกติแล้วถ้ารสชาติไม่อร่อยเด็กๆ จะไม่ค่อยทาน แต่ตัวนี้ ก่อนไปโรงเรียนเค้าจะไปขอแม่เค้าทานเองเลย เพราะ UMI มีรสชาติอร่อยเหมือนกับแอปเปิ้ลเขียว เปรี้ยวนิดๆ เป็นรสชาติที่อร่อย เด็กๆ ชอบรับประทานง่าย', '2'),
	(69, 1, 'คุณณัฐพล มานะวงศ์', 'พนักงานรัฐ จ.พิษณุโลก', 'ประทับใจในตัว UMI ครับ เพราะว่าผมได้แนะนำให้พ่อเพื่อนได้ทานพ่อเพื่อนเป็นโรคเบาหวานและเป็นโรคไตด้วย ถึงกับว่าต้องฉีดอินซูลินเข้าเส้นเลือดเลย พอผมแนะนำให้ใช้ ระยะเวลาเพียงแค่ 1 เดือนเท่านั้น เบาหวานที่เป็นก็กลับเป็นปกติเหมือนเดิม เหมือนคนปกติ แล้วก็ไตจากที่เมื่อก่อนหมอบอกว่า จะต้องฟอกไตก็ไม่ต้องฟอกแล้วครับ ขอบคุณครับ', '2'),
	(70, 1, 'คุณณัฐ พลเจริญสุข', 'อดีตพนักงานไอทีซัพพอร์ท บริษัทเมอร์ซิเดสเบนซ์', 'คุณแม่ใช้ UMI เมื่อก่อนช่วงรอยต่อของอากาศ คุณแม่จะต้องฉีดยาตลอดเลย แม่ผมเป็นโรคภูมิแพ้ ตอนนี้ใช้ UMI เพียงแค่เดือนเดียว พอช่วงรอยต่อของอากาศ ตอนนี้ก็ไม่ต้องฉีดยาแล้วครับ ขอบคุณ UMI ครับ', '2'),
	(71, 1, 'คุณพิราวรรณ์ เปรมศรีโรจน์', 'แม่บ้าน จ.ภูเก็ต', 'ตอนแรกนุ้ยก็อยู่กรุงเทพฯ แต่ได้มีโอกาสย้ายไปเป็นแม่บ้านที่ภูเก็ต ปรากฎว่าเป็นภูมิแพ้ จากคนที่เคยแข็งแรง โดนความชื้นสูงก็เลยไอตลอดทั้งวันทั้งคืนเลยทานยา รักษากับคุณหมอเป็นเวลา 6 เดือนก็ไม่ดีขึ้นเลย ใช้ทั้งยาพ่นจมูก แล้วก็ล้างจมูกด้วย จนสุดท้ายเกือบจะต้องไปผ่าตัดแล้ว แต่ว่าได้มาใช้ UMI กับ EXO ทำให้ยาต่างๆ หยุดได้ภายใน 1 เดือน ก็เลยประทับใจมากค่ะ', '1,2'),
	(72, 1, 'คุณวิศิษฐ์ สว่างทรัพย์', 'อดีตผู้อำนวยการโรงเรียนสารพัดช่าง จ.ชลบุรี', 'ตอนนี้ผมอายุ 66 ปีแล้วครับ ตามปกติผมมีสุขภาพที่แข็งแรง ไม่ค่อยป่วยและออกกำลังกายอย่างสม่ำเสมอ มาเมื่อ 6 ปีที่แล้ว หลังเกษียณราชการแล้ว ก็เริ่มมีอาการเหน็บชา ปวดที่แขนขวา ชา และปวดมากตั้งแต่แขนขวาถึงปลายนิ้ว ผมไปหาคุณหมอที่โรงพยาบาล หลังจากเอ็กซเรย์แล้ว คุณหมอแจ้งว่าเหตุมาจากกระดูกที่คอเสื่อม ต้องทานยา ผมทานยาตามที่คุณหมอสั่งมาแล้วก็ยังไม่ดีขึ้น ผมก็ไปหาคุณหมออีกโรงพยาบาลหนึ่ง คุณหมอที่นี่บอกว่าไม่ใช่กระดูกเสื่อมหรอก...เป็นพังผืด ต้องผ่าตัด ผมก็รับการผ่าตัดพังผืดตามคำแนะนำ หลังจากผ่าตัด อาการชา ปวด ก็ยังไม่หาย ผมขับรถไม่ได้เลยเพราะยังคงปวดมากผมไม่เคยนึกเลยว่าจะหายได้...เมื่อได้พบคุณหมอชนินทร์ คุณหมอแนะนำให้ทาน MIN แล้วจะดีขึ้น ปกติผมเชื่อคนยากมากผมคิดว่าไม่น่าจะหายได้เลย ซองเล็กๆ ดูสีก็สวยๆ แค่นี้นี่นะ แต่ก็คิดว่าจะลองดู บังเอิญขณะนั้นยังไม่มี MIN ในประเทศไทย ลูกชายคนโต (นิติ) ก็แนะนำให้ทาน OHM ก่อน ก็ดีขึ้นในระดับหนึ่งแต่ยังไม่หาย หลังจากนั้นผมก็ได้ MIN มาจากลูกผมทานไปประมาณ 40 ซอง แขนที่ชาและปวด ดีขึ้นมาเรื่อยๆถึง 95 - 97% ผมต้องขอบคุณ MIN มากครับและอยากแนะนำเพื่อนๆที่สูงอายุลองทานดูนะครับดีมากจริงๆ', '3'),
	(73, 1, 'คุณนพดล มากเจริญรุ่ง', 'ประกอบธุรกิจส่วนตัว', 'ตัวผมเองเป็นโรคธาลัสซีเมีย มีอาการอ่อนเพลียบ่อยๆ คุณหมอก็ได้แนะนำให้ผมทานกรดโฟลิก และวิตะมินบี 12 ผมทานกรดโฟลิก และวิตะมินบี 12 มาตลอดเป็นระยะเวลาพอสมควร อาการก็ยังคงปกติ พอผมได้มารู้จัก MIN ผมทานวันละ 1 ซองครัง ปัจจุบันผมทานมาประมาณ 20 กว่าวัน อาการที่อ่อนเพลียหรือแม้แต่หน้ามืดง่ายๆก็ดีขึ้นตามลำดับ ขอบคุณผลิตภัณฑ์ MIN ครับผม', '3'),
	(74, 1, 'คุณปิยมาศ พรหมสุวรรณ', 'จ.สุราษฏร์ธานี', 'ผลิตภัณฑ์ที่ใช้แล้วประทับใจก็คือ MIN ค่ะ เพราะว่ามีอยู่ช่วงหนึ่งดิฉันมีผมร่วงและผมบางซึ่งก็ไม่ทราบสาเหตุว่าเป็นเพราะอะไร ปรากฏว่าไปทานยาหมอที่หมอให้มา ผมก็มางอกขึ้น แถมขนยังขึ้นเต็มหน้าเลย แต่ว่าเผอิญได้มาฟังการแชร์ผลิตภัณฑ์ว่า MIN ช่วยเรื่องของเส้นผม ก็เลยลองทาน MIN ดู ปรากฏว่าทานไป 1 เดือน ผมที่เคยร่วงก็ไม่ร่วงแล้วก็มีผมงอกใหม่ขึ้นมาเต็มเลยค่ะ ขอบคุณ MIN ค่ะ', '3'),
	(75, 1, 'คุณชุติมาพร สิทธิพราหมณ์', 'ผู้จัดการสภาอุตสาหกรรม จ.สุราษฏร์ธานี', 'ผู้จัดการสภาอุตสาหกรรม จ.สุราษฏร์ธานี', '3'),
	(76, 1, 'คุณปภัส ธนายงธวุฒิ', 'นักศึกษาวิศวะ จ.นครราชสีมา', 'ผมเป็นโรคแพ้แดดเวลาเจอแดดจัดๆแล้วจะแสบตา พอผมได้มารู้จักและได้มาทาน MIN พอทานได้ไปประมาณ 10 กว่าวัน อาการแพ้แดดก็หายไป ขอบคุณ MIN มากครับ', '3'),
	(77, 1, 'Phorn Suthean', 'นักศึกษา กัมพูชา', 'ดิฉันเป็นคนที่เจ็บคอง่ายมาก มีวันนึงเจ็บคอมากพูดไม่ออกเลย คอแห้ง ก็เลยทาน MIN แล้วก็นอน พอตื่นเช้ามาก็สดชื่นค่ะ ขอบคุณ MIN ค่ะ', '3'),
	(78, 1, 'คุณทรงพล จิตตั้งบุญญา', 'อดีตผู้จัดการเขตประกันชีวิต', 'ผมประทับใจในตัว MIN และ EXO ครั้งเนื่องจากผมเป็นภูมิแพ้มาเป็นเวลานานตั้งแต่เด็ก ถ้าอากษเปลี่ยนก็จะทำให้เกิดอาการแพ้อย่างรุนแรงก็คือจะคัดจมูก จาม และจะเป็นแดงๆขึ้นบนใบหน้า พอได้ทาน MIN และ EXO คู่กันมาเป็นระยะเวลาประมาณ 1 เดือน อาการดังกล่าวไม่ว่าอากาศจะเปลี่ยนแปลงไปเท่าไหร่ก็ตาม ร่างกายก็แข็งแรงและก็ไม่เป็นหวัดอีกเลยครับ ขอบคุณ MIN และ EXO ครับ', '3'),
	(79, 1, 'คุณพิมสิริ หาญกิจรุ่ง', 'ประกอบธุรกิจส่วนตัว', 'ดิฉันป็นคนอายุมากแล้วนะคะ อายุ 56 ปีแล้ว เพราะฉะนั้นสุขภาพก็จะไม่ค่อยแข็งแรง ตัวที่ดิฉันประทับใจก็คือ MIN ค่ะ เพราะเป็นโรคเหน็บชาตื่นเช้ามาจะชามาก ก็เลยจะทาน MIN เป็นประจำ อีกตัวหนึ่งก็คือEXO ค่ะ อายุมากแล้วเราจะเป็นคนที่นอนไม่ค่อยหลับ ตื่นเช้ามาจะปวดหัว เพราะฉะนั้นจะทาน EXO ตื่นเช้ามาแล้วจะสบายค่ะ ขอบคุณ MIN และ EXO ค่ะ', '3'),
	(80, 1, 'คุณศิริวรรณ มีทอง', 'ผู้อำนวยการผฝ่ายการตลาด กรุงเทพฯ', 'พี่แมวทานตัว MIN ค่ะ เมื่อ 2 สัปดาห์ที่แล้วไปงานรับปริญญาน้องชายที่นิด้าแล้วก็นัดทานข้าวกันตอนเย็น หลายคนที่มาในงานก็ป่วยก็ทานข้าวร่วมกัน บนคนใช้ช้อนกลางบ้าง ไม่ใช้ช้อนกลางบ้าง ซึ่งเรามาทีหลังก็จอยด้วย พอทานข้าวเกือบจะเสร็จแล้ว ก็มีอาการรู้สึกเหมือนมีอะไรติดคอ ดื่มน้ำมันก็ยังไมออก พอขับรถกลับบ้านก็รู้สึกเจ็บคอขึ้นมาอีก เลยรู้ว่าไม่ใช่แล้ว นั่นเป็นอาการทอนซิลอักเสบ พอกลับถึงบ้านก็เลยทาน MIN แล้วหลับไปไม่ได้คิดอะไร ตื่นเช้าอาการเจ็บคอก็หายไป ทำไมวิเศษย่างนี้แค่คืนเดียวเอง แล้วก็อีกตัวหนึ่งค่ะคือลูกสาวพี่อายุ 12 ปี วัยนี้กำลังเป็นสิวเยอะ เค้าก็ไปรักษาที่สถาบันเกี่ยวกับเรื่องสิวหลายที่ คุณหมอที่โรงพยาบาลก็บอกว่าถ้าใช้ยาอย่างต่อเนื่องก็จะมีอันตรายกับร่างกาย จะลงตับหรืออะไรประมาณนี้ ก็เลยให้เค้าหยุดยาไปประมาณ 3 เดือน แล้วให้ลองใช้ Ageless ตัวคลีนซิ่งเจลล้างหน้า แล้วให้เค้าทาน EXO ด้วย ประมาณ 2 สัปดาห์เองสิวก็เริ่มหายแล้ว ตอนนี้ประมาณ 1 เดือนแล้วสิวก็หมด จางไปเหลือนิดหน่อยเท่านั้น ซึ่งกับเด็กวัยนี้เป็นวัยที่ฮอร์โมนกำลังทำงาน ก็คิดว่ามันเป็นอะไรที่มหัศจรรย์อีกตัวหนึ่ง เข้าใจว่ามันคงจะทำความสะอาดในเรื่องของการล้างหน้าได้ดีมากและก็ไม่มีสารตกค้างทำให้ไม่เป็นสิว ขอบคุณมากค่ะ ขอบคุณ EXO ขอบคุณ Ageless ขอบคุณ MIN ค่ะ', '3'),
	(81, 1, 'คุณอิทธิพัทธ์ สมบูรณ์ทรัพย์', 'ประกอบธุรกิจส่วนตัว', 'ผมประทับใจ SLM ครับ เพราะว่าหลังจากแนะนำเพื่อนแล้วเค้ารับประทาน SLM มาได้ 1 เดือน ปรากฏว่าใบหน้าของเค้าดีขึ้นดูกระชับขึ้น ตัวเองก็เลยประทับใจเลยมาทานเอง หลังจากนั้นก็รู้สึกว่าไขมันบริเวณต่างๆ บริเวณใบหน้าลดลงชัดเจน ห่างจากเพื่อนไปสัก 2 อาทิตย์ เพื่อนมาเจอเพื่อนทักเลยว่า “ผอมไปเยอะ พุงก็ยุบ” น้ำหนักผมลดลงประมาณ 2 – 3 กิโลกรัม ในระยะเวลาเพียว 1 เดือนครับ', '4'),
	(82, 1, 'คุณพีระพงศ์ แพงไพรี', 'อาจารย์มหาวิทยาลัยขอนแก่น', 'ประทับใจตัว SLM ครับ เพราะว่าปกติเป็นคนเล่นกีฬาอยู่แล้วเมื่อก่อนหุ่นก็จะดี แต่พอหลังจากที่หยุดเล่นกีฬาไปก็เริ่มมีพุงใหญ่มากทำยังไงก็ลดไม่ได้ พอมาเจอ SLM เพียงแค่สองสัปดาห์ก็ลดลงไป 5 กิโลกรัม หลังจากนั้นที่ประทับใจมากๆ ก็คือพอหยุดทาน SLM แล้ว สอนเดือนผ่านไปน้ำหนักก็ไม่ขึ้นอีกเลยครับ ขอบคุณ SLM ครับ', '4'),
	(83, 1, 'คุณวชิรเมษฐ์ นิติวัฒน์เอกธาดา', 'อดีตเจ้าของร้านเสื้อผ้าย่านรามคำแหง', 'แต่ก่อนผมไม่ค่อยมีความเชื่อมันในผลิตภัณฑ์ลดน้ำหนักสักเท่าไหร่ แล้วก็ได้ลองทานผลิตภัณฑ์อื่นๆมาบ้างแล้ว แต่เนื่องจากว่าเป็นคนที่ไม่ค่อยมีระเบียบวินัยในการรับประทานอาหารเสริมสักเท่าไหร่นัก วันนี้ได้มาเจอผลิตภัณฑ์ SLM ซึ่งซองแบบนี้ ทำให้พกพาสะดวกแล้วก็ทานง่าย จึงตัดสินใจลองทานดู ปรากฏว่าเพียงแค่ 1 อาทิตย์เท่านั้น ทำให้น้ำหนัก 80 กิโลกรัมของผมนั้นหายไป 5 กิโลกรัม ภายในระยะเวลาเพียวแค่ 1 อาทิตย์เท่านั้น ดังนั้นใครก็แล้วแต่ที่อยากจะทดลองความแปลกใหม่ อยากจะทดลองความสะดวกสบายกว่ามันสามารถให้ผลจริงรึเปล่า ผมอยากจะเชิญชวนทุกท่านนะครับก็อยากจะท้าท่านด้วยว่าวันนี้มาลองได้แล้วครับ เพราะผมเห็นผมด้วยตัวเองแล้ว 1 อาทิตย์ 5 กิโลกรัม', '4'),
	(84, 1, 'คุณกฤษนันท์ ศิริปิยะวัฒน์', 'นักศึกษา', 'ปกติน้ำหนักผมเคยสูงถึง 123 กิโลกรัม ผมก็ใช้วิธีออกกำลังกาย ผมออกกำลังกายประมาณ 6 เดือน น้ำหนักผมลดเหลือลงประมาณ 113 กิโลกรัม แต่พอผมได้ลองทาน SLM ผมทานควบคู่ไปกับการออกกำลังกาย ปรากฎว่าน้ำหนักผมก็ลดลงมาอีกตอนนี้เหลือ 105 กิโลกรัม ซึ่งมันช่วยให้เร็วขึ้นกว่าที่ผมจะออกกำลังกายเพียวอย่างเดียวมาก แล้วผมก็ได้ลองมาทุกอย่างแล้ว ไม่ว่าจะเป็นยาลดความอ้วนแบบไหน มันลดได้จริงครับแต่มันกดประสาท มันไม่หิว แต่มันโยโย่ครับ แต่สำหรับ SLM ถึงผมจะลืมทานไปบ้างแต่น้ำหนักก็ไม่ขึ้นเลยครับ อีกอย่างในเรื่องของการขับถ่าย ถ้าเป็นยาลดความอ้วนต่างๆที่ผมเคยทานมาจะถ่ายบ่อยและไม่เป็นเวลาครับ เวลาเดินทางก็ไม่สะดวก แต่ SLM นี่จะเป็นเวลาครับถ้าเกิดเราสะดวกตอนเช้าก็จะมาตอนเช้าไม่กระปิดกระปอยบ่อยครับ SLMครับ ดีมากครับ', '4'),
	(85, 1, 'คุณคเณศวร สวนบุรี', 'ประกอบธุรกิจส่วนตัว จ.ชัยภูมิ', 'ผลิตภัณฑ์ที่ชอบคือ SLM ครับ เพราะเมื่อก่อนผมหนัก 80 กิโลกรัม ผมก็ทาน SLM คู่กับ OHM เมื่อก่อนเคยลองทุกตัวแล้ว แต่พอมาเจอสองตัวนี้โอเคมากครับ มันรับประทานไม่ยาก ถ้าน้ำหนักเยอะก็ทานวันละ 2 ซอง ก่อนอาหาร 1 ชั่วโมง ผมทาน 2 ตัวนี้มาประมาณ 2 เดือนแล้ว อย่างถ้าเราทานตัวอื่นถ้าไม่ออกกำลังกายและทานเยอะเหมือนเดิม มันจะมีอาการโยโย่เอฟเฟค แต่สำหรับตัวนี้เราสามารถทานได้เต็มที่ครับ', '4'),
	(86, 1, 'คุณวุฒิชัย สกุลชัยพฤกษ์', 'วิศวกร จ.สงขลา', 'เมื่อก่อนผมมีปัญหาเรื่องน้ำนัก เราเป็นผู้ชายคิดว่าออกกำลังกายอย่างเดียวก็น่าจะเพียงพอแล้ว แต่ปรากฏว่าผมพยายามลดน้ำหนักด้วยการอดข้าวเย็นและออกกำลัยอย่างสม่ำเสมอ แต่พอ 2 เดือนผ่านไป ลดไปได้ 1 กิโลกรัม ซึ่งมันทำให้ผมท้อแท้มาก หลังจากนั้นผมมีโอกาสได้ลองทาน SLM ผมก็ปฏิบัติตัวในลักษณะเดิม เวลาผ่านมา 1 อาทิตย์ ตอนนี้น้ำหนักก็ลดลงไป 2 กิโลกรัม ขอบคุณ SLM มากครับ', '4'),
	(87, 1, 'จ๊ะจ๋า (พริมรตา เดชอุดม)', 'นักแสดง', 'จ๊ะจ๋าได้ใช้ผลิตภัณฑ์สารอาหารแบบเจลหลายตัว ที่ประทับใจก็มี OHM,MIN,UMI อย่างตัว OHM ปกติจ๊ะจ๋า จะเป็นคนมีพลังมาก ตื่นแต่เช้าไปกองถ่าย จนถึงดึก กลับบ้านบางครั้งรู้สึกเพลีย เหนื่อยล้ามากไม่มีสมาธิในการจำบทเลย เมื่อได้ทดลองทาน OHM ก็รู้สึกสดชื่นแทบจะทันที สามารถทำงานได้ทั้งวัน จ๊ะจ๋าต้องพกติดตัวตลอดเวลา โดยส่วนตัวจ๊ะจ๋าเป็นคนรักสุขภาพ แต่รู้ตัวว่าพักผ่อนน้อย ทานอาหารไม่ครบ 5 หมู่ จ๊ะจ๋าก็ต้องรับประทานวิตะมินหลายชนิดมาก ทั้ง A,C,D,E,B1 – 6 -12 และอีกมากมายต้องพกเป็นกระปุก เพื่อบำรุงผิว,ผม,เล็บ,สายตา,สมอง ให้ดี มีอยู่วันหนึ่งได้รู้จัก MIN จากน้องพิมพ์บอกว่า พี่เชยมากรับประทานวิตามินเป็นกระปุกขนาดนี้ จ๊ะจ๋าก็ได้ลองทานดู จากที่คิดว่าน่าจะทดแทนของเดิมได้ไม่หมด แต่กลับไม่เป็นเช่นนั้น จ๊ะจ๋าก็ยังสดใสและมีพลังเหมือนเดิม กลับจะมากขึ้นด้วย ที่ชอบมากคือรู้สึกว่ามีความสุขมากไม่ต้องทนกับการกินวิตะมินให้ติดคอและทานน้ำตามเยอะมาก อีกตัวหนึ่งคือ UMI จ๊ะจ๋าทานทุกเช้า เพราะปกติเป็นโรคภูมิแพ้ แพ้ฝุ่น ตอนนี้รู้สึกสบายมากถ้าต้องเจอกับฝุ่นหรืออากาศที่เปลี่ยน เพราะผู้ช่วยตัวนี้คะ', '2,3,4'),
	(88, 1, 'คุณศิรประภา ไสยนะพันธ์', 'เลขานุการ จ.นครราชสีมา', 'ผลิตภัณฑ์สารอาหารแบบเจล ฝนประทับใจทุกตัวเลย แต่ตัวที่เห็นผลเร็วที่สุดคือ OHM คือฝนเองเป็นคนที่เดินทางบ่อย และขับรถกลางคืนด้วยแล้วก็หลายๆครั้งก็รู้สึกง่วงและเพลียมากๆ แต่พอได้ OHM เห็นผลไวมากแค่รับประทานหลังอาหาร 1 ชั่วโมงเท่านั้น มันจะดึงโปรตีนคาร์โบไฮเดรตที่เราได้จากอาหารมาบูธให้เป็นพลังงานแบบเร่งด่วน แล้วจะอยู่อย่างนั้น 6 ชั่วโมง คือ สังเกตเห็นง่ายมากๆเลย ตอนที่เราขับรถอยู่แล้วง่วง ฉีกตัวนี้กินปุ๊บตาสว่าง แล้วก็ขับรถได้เร็วแล้วหลายชั่วโมงเลยค่ะ', '5'),
	(89, 1, 'เดสมอนด์ โปห์', 'เจ้าของธุรกิจ จากสิงคโปร์', 'ผมมีปัญหาเรื่องคอลเลสเตอรอลสูง วันหนึ่งได้รู้จัก HRT และลองทานดูหลังจากนั้นได้ไปตรวจเช็คร่างกาย พบว่าปริมาณคอเลสเตอรอลลดลง จนผมประหลาดใจมากเพราะตลอด 10 ปีที่ผ่านมา ผมมีคอลเลสเตอรอลที่สูงมากถึง 280 แม้ว่าจะกินยาสม่ำเสมอก็ไม่เคยคุมคอเลสเตอรอลได้เลย ล่าสุดปัจจุบันเหลือเพียง 240 หลังจากที่ได้ทาน HRT ได้เพียง 3 เดือน ผมประทับใจมาก และจะทานต่อไปอย่างแน่นอนครับ', '7'),
	(90, 1, 'คุณสกล ใบโสภณ', 'วิศวกรคอมพิวเตอร์', 'ด้วยงานที่หนักทให้ผมไม่มีโอกาสออกกำลังกาย ล่าสุดผมไปตรวจร่างกายทำให้ผมทราบว่าคนตัวเล็กๆอย่างผมก็มีระดับคอเลสเตอรอลสูงมาก ทำให้เรารู้สึกว่า เราจะต้องดูแลตัวเองแล้ว โชคดีครับที่ผมเจอ HRT หลังจากที่ผมทาน HRT ไปประมาณ 1 กล่องเท่านั้น ผมก็ไปตรวจอีกครั้ง ปราดฏว่าผลเลือดระดับคอลเลสเตอรอล ทุกอย่างลงมาหมดอยู่ในเกณฑ์ที่ดีมากครับ ต้องขอบคุณ HRT ครับ', '7'),
	(91, 1, 'คุณดวงจันทร์ พรีฉัตรปกรณ์', 'ตัวแทนประกันชีวิต จ.สงขลา', 'เมื่อ 1 ปีที่ผ่านมาตัวเองมีปัญหาเรื่อความดันสูง ตื่นตอนเช้าเหมือนกับไม่ได้นอน ช่วงบ่าย 3 – 4 โมงของทุกวันจะรู้สึกเหนื่อยแล้วก็ไม่อยากทำงาน อยากกลับบ้าน แต่เมื่อรู้จัก HRT ทานไปเพียง 2 วัน ตื่นมาตอนเช้าก็ไม่มีอาการอ่อนเพลียหลงเหลืออยู่เลย แล้วก็หลังจากนั้นทำงานถึงสี่ห้าทุ่มเที่ยงคืนก็ไม่มีอาการอ่อนเพลีย รู้สึกว่าตัวเองมีคุณภาพชีวิตที่ดีขึ้น ขอบคุณ HRT มากๆ ค่ะ', '7'),
	(92, 1, 'คุณวิรัตย์ ชาญธัญกรรม', 'เจ้าของกิจการรถบรรทุก 10 ล้อ จ.ราชบุรี', 'ตอนนี้ผมอายุ 55 ปีแล้ว ผมประทับใจในตัว HRT ครับ คือผมจะเป็นคนที่เหนื่อยง่าย ขึ้นบันไดก็เหนื่อย นอนตื่นขึ้นมาก็มือไม้ชา ลุกขึ้นืนก็หน้ามืด นี่เราไปโรคหัวใจโดยไม่รู้ตัว ผมทาน HRT ประมาณครึ่งกล่อง ผมก็หาย และผมก็ประทับใจอีกตัวคือ FLX ครับ เพราะตอนนี้ผมข้อเข่าไม่ค่อยดี ทานแล้วอาการดีขึ้นเยอะเลยครับ ขอบคุณ HRT และ FLX ครับ', '7'),
	(93, 1, 'คุณศิษฏา ไตรโสภณ', 'อดีตพนักงานประจำ', 'เมื่อก่อนทำงานประจำจะต้องเข้าโรงพยาบาลตลอดเลย ทุกอาทิตย์เลยก็ว่าได้พอมากินตัว HRT ได้ประมาณ 1 เดือน ตอนนี้ไม่ต่องเข้าโรงพยาบาลอีกแล้วค่ะ ต้องขอบคุณ HRT มากๆเลยค่ะ', '7'),
	(94, 1, 'คุณกฤติยาณี จันทรากูร', '', 'เมื่อประมาณ 4 ปีที่แล้วป่วยหนัก พบว่ามีหมอนรองกระดูกที่ต้นคอที่ 3 ที่ 4 แตกร้าว แล้วก็มีของเหลวไหลออกมาไประคายเส้นประสาททำให้ชาไปครึ่งตัว หลังจากนั้นได้เข้ารับการผ่าตัด เปลี่ยนหมอนรองกระดูกที่ต้นคอ เป็นหมอนรองกระดูกเทียม จากนั้นต้องทานยาเกี่ยวกับกระดูกเป็นเวลา 1 ปี หลังจากที่ผ่าตัดได้ 1 เดือน มีเสียในหู มันทรมานมาก ตอนกลางคืนจะทำให้เรานอนไม่หลับ พอเปลี่ยนมาอีกหมอหนึ่งก็เกิดอาการโรคบ้านหมุน คุณหมอบอกว่ามันเกี่ยวกับตะกอนในหูชั้นใน ซึ่งมันเป็นสิ่งที่ทำให้เราเสียสุขภาพ โดยรวมแล้วทานยาถึง 3 หมอ ได้มีโอกาสรู้จักกับคุณหมอชนินทร์ ท่านแนะนำให้รู้จักสารอาหารแบบเจล ตอนนี้ทานผลิตภัณฑ์สารอาหารแบบเจลเพื่อสุขภาพของตัวเองทุกตัวค่ะ และโชคดีที่ลูกสาวอยู่อเมริกา ลูกสาวได้นำ FLX มาให้ทานเป็นเวลา 1 เดือน หลังจากทานผลิตภัณฑ์สารอาหารแบบเจลประมาณ 1 เดือน ก็เลิกยาคุณหมอได้ทุกตัวเลยค่ะ ปัจจุบันได้ทานสารอาหารแบบเจลแบบกระหน่ำมาก ทานทุกตัว แล้วก็เลิกยาของหมอได้หมดเลยค่ะ ขอบคุณค่ะ', '7'),
	(95, 1, 'คุณบุญชวน เปรมเครือ', 'พนักงานบริษัท', 'จากเมื่อก่อนผมจะปวดช่วงล่าง เนื่องจากยกของหนักบ่อยสมัยเป็นเด็ก พอมาเจอ FLX มันสามารถช่วยได้ พอใช้ได้ประมาณ 2 – 3 วัน อาการก็ดีขึ้นแล้วครับ ทำให้ผมติดใจผลิตภัณฑ์ตัวนี้มาก ขอบคุณครับ', '7'),
	(96, 1, 'คุณศุภเดช หอละเอียด', 'รับราชการตำรวจ จ.กาญจนบุรี', 'คุณพ่ออายุ 77 ปี เป็นข้อเข่าเสื่อม คอเรสเตอรอลสูง ไขมันในเส้นเลือดสูง ไตรกรีเซอไรด์สูง จากที่หัวเข่าเดินไม่ได้มา 10 กว่าปีแล้ว พอทานตัว FLX มาสัก 15 ซองก็รู้สึกว่าดีขึ้น เดินคล่องขึ้น ทานต่อเนื่องประมาณ 5 – 6 เดือนแล้ว ตอนนี้คุณพ่อไม่ต้องใช้รถเข็นแล้ว ส่วนที่เป็นคอเรสเตอรอลสูง มีอาการเวียนศรีษาอยู่ตลอดเวลา ก็ให้ทาน HRT ก็ทำให้คุณพ่อรู้สึกไม่เวียนหัวมีหน้าตาที่สดใส มีเลือดฝาดบนใบหน้า ก็ต้องขอบคุณสารอาหารแบบเจลด้วยที่ทำให้คุณพ่อดีขึ้นมาก', '6,7'),
	(97, 1, 'คุณดวงกลม ฉิมพัฒน์', 'ธุรกิจส่วนตัวร้านกาแฟ', 'ผลิตภัณฑ์ที่ประทับใจก็คือตัว FLX ค่ะ เป็นเรื่องของคุณพ่อ คุณพ่อเป็นโรคเกี่ยวกับหมอนรองกระดูไม่สามารถที่จะขับรถได้ นั่งนานก็ไม่ได้ คุณหมอไม่ให้ขยับตัวแรงๆแล้วก็นัดผ่าตัดในเดือนถัดไป พอมาพบกับตัว FLX เลยให้คุณพ่อทาน คุณพ่อบอกว่าทานได้ 15 วันก็ลืมไปเลยว่ามีอาการปวดหลังอยู่ ถึงวันนี้คุณพ่อก็ไม่ต้องผ่าตัดแล้ว ขอบคุณ FLX ค่ะ', '6'),
	(98, 1, 'นพสิทธิ์ เจตสินไพศาล', 'เจ้าของธุรกิจส่วนตัว', 'ผมมีปัญหาเรื่องกระดูก 4 – 5 เคลื่อนและชาแขนซ้าน เวลานอนแล้วพลิกตัวจะนอนหอบหายใจหนักมาก ดึงคอไป 15 ครั้งแล้วได้รับการแนะนำจากคุณหมอให้ทาน MIN และ FLX ไม่น่าเชื่อผ่านไป 3 เดือน ตอนนี้ผมหาย 100% ไม่อยากเชื่อก็ต้องเชื่อร่างกายของคนเรารักษาตัวเองได้จริงๆ', '3,6'),
	(99, 1, 'พริมรตา เดชอุดม (จะจ๋า)', 'นักแสดง', 'ที่ประทับใจอีกชุด ต้องเรียกว่าชุดเพราะมีเยอะมาก คือ Ageless ตัวนี้ต้องขอขอบคุณคุณหมอชนิทนร์และคุณแม่เป้ มากๆที่แนะนำให้ได้รู้จัก ปกติเป็นคนที่ใช้สินค้าแบรนด์เพื่อดูแลผิวพรรณอยู่แล้ว แต่ได้ทดลองใช้ Ageless ตอนที่หน้าของจ๊ะจ๋าแย่มาก ตอนถ่ายหนังเรื่ององค์บากที่ระยอง ที่เกิดสิวเยอะมากที่หน้าผากและแก้ม และเป็นฝ้าอยู่ด้านหนึ่ง จ๊ะจ๋าตัดสินใจทดลองใช้และคิดว่าถ้าผลิตภัณฑ์ดีจริง จะต้องช่วยทำให้หน้าดีขึ้นภายใน 7 วัน ปรากฎว่า 7 วันผิวหน้าดีขึ้นมากจริงๆ ประทับใจมากและจากนั้นก็ใช้มาตลอด ดีมากทั้งสครับใช้แล้วหน้าไม่แห้งเลย อีกอย่างคือคลีนซิ่ง เจล ตัวนี้ต้องพกติดตัวไปกองถ่ายด้วยตลอด ปัจจุบันผิวหน้าของจ๊ะจ๋าดีขึ้นมาก ริ้วรอยลดลงมาก ใครที่ไม่เคยทดลองใช้ อยากให้ได้ทดลองใช้ ไม่ต้องรออายุมากแล้วถึงใช้ อยากจะบอกว่าทุกคนที่จะต้องอยู่หน้าจอคอมเยอะๆ,เราต้องนอนดึก,ผิวแพ้ง่าย ผิวมัน เราสามาถแก้ปัญหาต่างๆได้ด้วยผลิภัณฑ์ชุดนี้ ควบคู่ไปกับการรับประทานอาหารที่ดี ทานวิตะมิน นอนหลับให้เพียงพอ จ๊ะจ๋าไม่รู้สึกผิดหวังเลยที่ใช้ผลิตภัณฑ์ Agel ต้องขอบคุณค่ะ', '15'),
	(100, 1, 'คุณณัฐชานันท์ อชิรากรวิวัฒน์', 'แม่บ้าน จ.สมุทรปราการ', 'จริงๆแล้วประทับใจสารอาหารแบบเจลหลายตัวเลยนะคะ แต่วันนี้จะมาแชร์ตัว Ageless ค่ะ คือตุ๊กเป็นคนที่แพ้ง่ายมากๆ ไม่กล้าใช้ครีมบำรุง จนกระทั่งถึงตอนนี้อายุหลักสี่แล้ว ก็จะไม่ได้ใช้บำรุงผิวเลย พอมาลองใช้ Ageless ก็ประทับใจมากๆ คือเราจะมี กระ มีฝ้าดำๆ และมีริ้วรอยตามวัย แต่ Ageless ทำให้หน้าเราดีขึ้นเพราะแต่ก่อนหน้าจะแย่มาก พอใช้ได้ไม่ถึงเดือนก็เริ่มเห็นความแตกต่างแล้วที่สำคัญ ประทับใจตรงที่ตัวคลีนซิ่ง ใช้มาตั้งแต่เดือนสิงหาคมถึงตอนนี้เดือนมกราคม ผลิตภัณฑ์ลดลงไปแค่ครึ่งขวด คือทำให้เราใช้ได้นานและประหยัดด้วย ขอบคุณ Ageless ค่ะ', '15'),
	(101, 1, 'Phorn Suthun', 'กัมพูชา', 'เมื่อก่อนผิมหน้าผมจะลอกและมีอาการคัน แต่ก็ไม่ได้สังเกตอะไร พอเป็นมาได้สัก 1 เดือน ก็เห็นว่าทำไมเป็นนานจัง ตัวผมเองเป็นคนที่ไม่มีวินัยในการใช้ผลิตภัณฑ์อยู่แล้วด้วย ใช้บ้างไม่ใช้บ้าง ตอนนั้นก็เลยหยิบ Ageless มาใช้ติดต่อกัน 4 วัน อาการดังกล่าวก็หายไป ขอบคุณ Ageless ครับ', '15');
